import React from 'react';
import styled from 'styled-components';

const UserDetails = ({ fullName, email, createdAt, username }) => (
  <DetailsCard>
    <Title>Detalhes do Usuário</Title>
    <DetailItem>
      <Label>Nome:</Label>
      <Value>{fullName || 'Nome não disponível'}</Value>
    </DetailItem>
    <DetailItem>
      <Label>Email:</Label>
      <Value>{email || 'Email não disponível'}</Value>
    </DetailItem>
    <DetailItem>
      <Label>Data de Criação:</Label>
      <Value>{formatDate(createdAt) || 'Data não disponível'}</Value>
    </DetailItem>
    <DetailItem>
      <Label>Nome de Usuário:</Label>
      <Value>{username || 'Nome de usuário não disponível'}</Value>
    </DetailItem>
  </DetailsCard>
);

const DetailsCard = styled.div`
  background-color: #2c2c2c;
  padding: 30px;
  border-radius: 15px;
  color: #ffffff;
  box-shadow: 0 6px 18px rgba(0, 0, 0, 0.4);
  max-width: 600px;
  margin: 30px auto;
  transition: transform 0.2s, box-shadow 0.2s;

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 10px 20px rgba(0, 0, 0, 0.5);
  }

  @media (max-width: 768px) {
    padding: 25px;
    margin: 20px;
  }
`;

const Title = styled.h2`
  margin: 0 0 20px;
  font-size: 2rem;
  color: #ea4f97;
  text-align: center;
  border-bottom: 3px solid #333;
  padding-bottom: 10px;
`;

const DetailItem = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 15px 0;
  border-bottom: 1px solid #444;

  &:last-child {
    border-bottom: none;
  }
`;

const Label = styled.span`
  font-weight: 600;
  color: #ea4f97;
  flex: 1;
  text-align: right;
  margin-right: 20px;
  font-size: 1.1rem;
`;

const Value = styled.span`
  flex: 2;
  text-align: left;
  font-size: 1rem;
  color: #d4d4d4;
`;

const formatDate = (dateString) => {
  const options = { day: '2-digit', month: '2-digit', year: 'numeric' };
  return new Date(dateString).toLocaleDateString('pt-BR', options);
};

export default UserDetails;
