// Intro.js
import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';

const Intro = () => {
  return (
    <IntroContainer>
      <ContentWrapper>
        <Title>Connecter</Title>
        <Subtitle>O gerenciador de tarefas que você sempre quis.</Subtitle>
        <CardContainer>
          <Card>
            <PhoneImage src="https://via.placeholder.com/200x400" alt="App screenshot 1" />
          </Card>
          <Card>
            <PhoneImage src="https://via.placeholder.com/200x400" alt="App screenshot 2" />
          </Card>
          <Card>
            <PhoneImage src="https://via.placeholder.com/200x400" alt="App screenshot 3" />
          </Card>
        </CardContainer>
        <LoginButton to="/login">Iniciar Sessão</LoginButton>
      </ContentWrapper>
    </IntroContainer>
  );
};

const IntroContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  text-align: center;
  padding: 2rem;
  min-height: 100vh;
  background: #fff; /* Fundo branco para um visual limpo */
`;

const ContentWrapper = styled.div`
  max-width: 1200px;
  width: 100%;
`;

const Title = styled.h1`
  font-size: 4rem;
  color: #000;
  font-weight: bold;
  margin-bottom: 1rem;
  font-family: 'Open Sans', sans-serif;
`;

const Subtitle = styled.p`
  font-size: 1.5rem;
  color: #666;
  margin-bottom: 2rem;
  font-family: 'Open Sans', sans-serif;
`;

const CardContainer = styled.div`
  display: flex;
  justify-content: space-around;
  gap: 1rem;
  margin-bottom: 2rem;
`;

const Card = styled.div`
  background: #f8f8f8;
  border-radius: 10px;
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1);
  padding: 1rem;
  max-width: 200px;
  text-align: center;
  flex: 1;
`;

const PhoneImage = styled.img`
  width: 100%;
  border-radius: 10px;
  object-fit: cover;
`;

const LoginButton = styled(Link)`
  background-color: #000;
  color: #fff;
  border: none;
  padding: 0.75rem 2rem;
  border-radius: 20px;
  font-size: 1.1rem;
  font-weight: 600;
  cursor: pointer;
  transition: background-color 0.3s;
  text-decoration: none;

  &:hover {
    background-color: #555;
  }
`;

export default Intro;
