// TaskModal.js
import React, { useState, useEffect } from 'react';

const styles = {
  modal: {
    position: 'fixed',
    top: '0',
    left: '0',
    width: '100%',
    height: '100%',
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    zIndex: 9999, // z-index altíssimo para garantir que o modal fique no topo
    overflow: 'hidden',
  },
  modalContent: {
    background: '#fff',
    padding: '20px',
    borderRadius: '4px',
    width: '300px',
    maxWidth: '90%',
    zIndex: 10000, // z-index ainda mais alto para o conteúdo do modal
    position: 'relative',
  },
  input: {
    width: '100%',
    padding: '8px',
    marginTop: '5px',
  },
  button: {
    marginRight: '10px',
  },
};

const TaskModal = ({ task, newTaskTitle, newTaskDescription, setNewTaskTitle, setNewTaskDescription, onUpdate, onClose, onAdd }) => {
  const [title, setTitle] = useState(newTaskTitle || '');
  const [description, setDescription] = useState(newTaskDescription || '');

  useEffect(() => {
    if (task) {
      setTitle(task.title);
      setDescription(task.description);
    }
  }, [task]);

  const handleSubmit = () => {
    if (task) {
      onUpdate({ ...task, title, description });
    } else {
      onAdd();
    }
  };

  useEffect(() => {
    // Bloquear rolagem da página quando o modal está aberto
    document.body.style.overflow = 'hidden';
    return () => {
      document.body.style.overflow = 'auto'; // Restaurar rolagem quando o modal é fechado
    };
  }, []);

  return (
    <div style={styles.modal}>
      <div style={styles.modalContent}>
        <h2>{task ? 'Editar Tarefa' : 'Adicionar Tarefa'}</h2>
        <label>
          Título:
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            style={styles.input}
          />
        </label>
        <label>
          Descrição:
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            style={{ ...styles.input, height: '100px' }}
          />
        </label>
        <button style={styles.button} onClick={handleSubmit}>{task ? 'Atualizar' : 'Adicionar'}</button>
        <button style={styles.button} onClick={onClose}>Fechar</button>
      </div>
    </div>
  );
};

export default TaskModal;
