import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { FaArrowRight } from 'react-icons/fa';
import { useAuth } from '../Context/AuthContext';
import { useFormState } from '../hooks/useFormStates';
import { useLocation } from 'react-router-dom'; // Importa o hook useLocation
import Logo from '../components/Login/Logo';
import Title from '../components/Login/Title';
import EmailContainer from '../components/Login/EmailContainer';
import PasswordContainer from '../components/Login/PasswordContainer';
import Input from '../components/Login/Input';
import Button from '../components/Login/Button';
import RegisterLink from '../components/Login/RegisterLink';
import ErrorMessage from '../components/Login/ErrorMessage';

const LoginForm = () => {
  const { login, error: authError } = useAuth();
  const { formValues, formError, handleInputChange, clearError } = useFormState({
    email: '',
    password: '',
  });
  const [showPassword, setShowPassword] = useState(false);
  const [loadingStartTime, setLoadingStartTime] = useState(null);
  const [minLoadingDuration] = useState(5000);
  const location = useLocation(); // Hook para detectar mudanças de rota

  useEffect(() => {
    // Limpa os erros ao navegar
    clearError();
  }, [location.pathname, clearError]); // Limpa erros quando o caminho mudar

  useEffect(() => {
    if (formError) {
      const timeoutId = setTimeout(() => {
        clearError(); // Limpa o erro após um tempo
      }, 3000); // Ajuste o tempo conforme necessário

      return () => clearTimeout(timeoutId); // Limpa o timeout quando o componente desmontar
    }
  }, [formError, clearError]);

  const validateEmail = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const handleShowPassword = () => {
    if (validateEmail(formValues.email)) {
      setShowPassword(true);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoadingStartTime(Date.now());
    clearError();

    try {
      await login(formValues.email, formValues.password);
      const elapsedTime = Date.now() - loadingStartTime;
      const remainingTime = Math.max(minLoadingDuration - elapsedTime, 0);
      // Implementação adicional para lidar com remainingTime, se necessário
    } catch (err) {
      console.error('Erro ao fazer login:', err);
    }
  };

  const handleChange = (e) => {
    handleInputChange(e);
    clearError();
  };

  return (
    <Container>
      <Form onSubmit={handleSubmit}>
        <Logo />
        <Title>Faça login com seu ID Connecter</Title>
        <EmailContainer>
          <Input
            name="email"
            type="email"
            placeholder="E-mail"
            value={formValues.email}
            onChange={handleChange}
            autoFocus
          />
          {!showPassword && (
            <Button
              onClick={handleShowPassword}
              disabled={!validateEmail(formValues.email)}
              emptyEmail={!formValues.email}
            >
              <FaArrowRight />
            </Button>
          )}
        </EmailContainer>
        <PasswordContainer show={showPassword}>
          <Input
            name="password"
            type="password"
            placeholder="Senha"
            value={formValues.password}
            onChange={handleChange}
          />
          <Button type="submit">
            <FaArrowRight />
          </Button>
        </PasswordContainer>
        <RegisterLink to="/register">Criar um ID Connecter</RegisterLink>
        {formError && <ErrorMessage>{formError}</ErrorMessage>}
      </Form>
    </Container>
  );
};

const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 60vh;
  margin-top: 9rem;
`;

const Form = styled.form`
  padding: 2rem;
  max-width: 560px;
  width: 100%;
  text-align: center;
  background: #1c1c1c;
  border-radius: 30px;
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.4);
`;

export default LoginForm;
