import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import styled from 'styled-components';
import { useAuth } from '../Context/AuthContext';

const formatDate = (dateString) => {
  const options = { day: '2-digit', month: '2-digit', year: 'numeric' };
  return new Date(dateString).toLocaleDateString('pt-BR', options);
};

const TasksPage = () => {
  const { username } = useParams();
  const [tasks, setTasks] = useState([]);
  const [selectedTask, setSelectedTask] = useState(null);
  const [fullName, setFullName] = useState('');
  const [email, setEmail] = useState('');
  const [createdAt, setCreatedAt] = useState('');
  const { token, userInfo } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserInfo = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/users/user/${username}`);
        if (response.ok) {
          const data = await response.json();
          setFullName(data.fullName || 'Nome não disponível');
          setEmail(data.email || 'Email não disponível');
          setCreatedAt(formatDate(data.createdAt) || 'Data não disponível');
        } else {
          console.error('Erro ao carregar informações do usuário:', response.statusText);
        }
      } catch (error) {
        console.error('Erro ao carregar informações do usuário:', error);
      }
    };

    const fetchTasks = async () => {
      try {
        const apiUrl = token
          ? `http://localhost:5000/api/manager/tasks/${encodeURIComponent(userInfo.username)}`
          : `http://localhost:5000/api/manager/tasks/${encodeURIComponent(username)}`;

        const response = await fetch(apiUrl, {
          headers: token ? { Authorization: `Bearer ${token}` } : {},
        });

        if (response.ok) {
          const data = await response.json();
          // Ordena as tarefas por data de criação em ordem decrescente
          const sortedTasks = data.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
          // Limita às 9 tarefas mais recentes
          const recentTasks = sortedTasks.slice(0, 9);
          setTasks(recentTasks);
        } else {
          console.error('Erro ao carregar tarefas:', response.statusText);
        }
      } catch (error) {
        console.error('Erro ao carregar tarefas:', error);
      }
    };

    fetchUserInfo();
    fetchTasks();
  }, [navigate, username, token, userInfo.username]);

  const handleViewTask = (task) => setSelectedTask(task);

  const handleCloseModal = () => setSelectedTask(null);

  return (
    <PageContainer>
      <Header>
        <h1>Dashboard de {fullName}</h1>
        <p>Visualize suas tarefas e detalhes do usuário</p>
      </Header>
      <Content>
        <UserDetails>
          <h2>Informações do Usuário</h2>
          <p><strong>Usuário:</strong> {username}</p>
          <p><strong>Email:</strong> {email}</p>
          <p><strong>Data de Criação:</strong> {createdAt}</p>
        </UserDetails>
        <TasksSection>
          <h2>Suas Tarefas</h2>
          {tasks.length > 0 ? (
            <TasksList>
              {tasks.map((task) => (
                <TaskCard key={task.id}>
                  <TaskTitle>Título: {task.taskTitle}</TaskTitle>
                  <TaskInfo>Criada em: {formatDate(task.createdAt)}</TaskInfo>
                  <ViewButton onClick={() => handleViewTask(task)}>Ver Tarefa</ViewButton>
                </TaskCard>
              ))}
            </TasksList>
          ) : (
            <NoTasksMessage>Não há tarefas disponíveis.</NoTasksMessage>
          )}
        </TasksSection>
      </Content>

      {selectedTask && (
        <ModalOverlay onClick={handleCloseModal}>
          <ModalContent onClick={(e) => e.stopPropagation()}>
            <ModalHeader>
              <h3>
                <TitleLabel>Título:</TitleLabel>
                <TaskTitle>{selectedTask.taskTitle}</TaskTitle>
              </h3>
              <CloseButton onClick={handleCloseModal}>×</CloseButton>
            </ModalHeader>
            <ModalBody>
              <p><strong>Descrição:</strong> {selectedTask.taskDescription}</p>
              <p><strong>Status:</strong> {selectedTask.taskStatus ? 'Concluído' : 'Pendente'}</p>
            </ModalBody>
          </ModalContent>
        </ModalOverlay>
      )}
    </PageContainer>
  );
};

const PageContainer = styled.div`
  background-color: #111;
  color: #f5f5f5;
  padding: 20px;
  min-height: 100vh;
  font-family: 'Roboto', sans-serif;

  @media (max-width: 768px) {
    padding: 15px;
  }
`;

const Header = styled.header`
  text-align: center;
  margin-bottom: 30px;
  margin-top: 5rem;

  h1 {
    font-size: 2.5rem;
    margin-bottom: 10px;
  }

  p {
    color: #bbb;
  }

  @media (max-width: 768px) {
    h1 {
      font-size: 2rem;
    }
  }
`;

const Content = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;

  @media (min-width: 768px) {
    flex-direction: row;
  }
`;

const UserDetails = styled.section`
  flex: 1;
  background-color: #222;
  padding: 20px;
  border-radius: 8px;
  text-align: left;
  height: auto;

  h2 {
    margin-bottom: 15px;
    font-size: 1.5rem;
  }

  p {
    margin-bottom: 10px;
    font-size: 1rem;

    strong {
      color: #ea4f97;
    }
  }

  @media (max-width: 768px) {
    padding: 15px;
  }
`;

const TasksSection = styled.section`
  flex: 2;
  background-color: #222;
  padding: 20px;
  border-radius: 8px;

  h2 {
    margin-bottom: 15px;
    font-size: 1.5rem;
  }

  @media (max-width: 768px) {
    padding: 15px;
  }
`;

const TasksList = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(220px, 1fr));
  gap: 15px;

  @media (max-width: 600px) {
    grid-template-columns: repeat(1, 1fr);
  }
`;

const TaskCard = styled.div`
  background-color: #333;
  padding: 20px;
  border-radius: 8px;
  position: relative;
  transition: transform 0.3s, box-shadow 0.3s;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.3);

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 8px 16px rgba(0, 0, 0, 0.4);
  }

  @media (max-width: 768px) {
    padding: 15px;
  }
`;

const TaskTitle = styled.h3`
  margin: 0;
  color: #fff;
  font-size: 1.25rem;
`;

const TaskInfo = styled.p`
  color: #aaa;
  font-size: 0.9rem;
  margin-top: 5px;
  margin-bottom: 3rem;
`;

const ViewButton = styled.button`
  background-color: #ea4f97;
  color: #fff;
  border: none;
  padding: 10px 15px;
  border-radius: 5px;
  cursor: pointer;
  font-size: 0.9rem;
  position: absolute;
  bottom: 10px;
  left: 10px;
  transition: background-color 0.3s;

  &:hover {
    background-color: #f291be;
  }
`;

const NoTasksMessage = styled.p`
  text-align: center;
  color: #888;
  margin-top: 20px;
`;

const ModalOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.8);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 1000;
`;

const ModalContent = styled.div`
  background-color: #222;
  padding: 20px;
  border-radius: 8px;
  max-width: 600px;
  width: 100%;
  position: relative;

  @media (max-width: 768px) {
    padding: 15px;
  }
`;

const ModalHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 15px;
`;

const TitleLabel = styled.span`
