// EditTaskModal.js
import React, { useEffect } from 'react';
import styled from 'styled-components';
import useFormManager from '../../hooks/Manager Hooks/useFormManager';

const EditTaskModal = ({ isOpen, onClose, onSave, task }) => {
  const { formData, handleChange, resetForm } = useFormManager({
    title: task ? task.taskTitle : '',
    description: task ? task.taskDescription : '',
  });

  useEffect(() => {
    if (task) {
      resetForm({
        title: task.taskTitle,
        description: task.taskDescription,
      });
    }
  }, [task, resetForm]);

  const handleSubmit = () => {
    onSave(formData);
    handleClose();
  };

  const handleClose = () => {
    resetForm();
    onClose();
  };

  if (!isOpen) return null;

  return (
    <ModalOverlay>
      <ModalContent>
        <Title>Editar Tarefa</Title>
        <Input
          type="text"
          name="title"
          placeholder="Título"
          value={formData.title}
          onChange={handleChange}
        />
        <Textarea
          name="description"
          placeholder="Descrição"
          value={formData.description}
          onChange={handleChange}
        />
        <ButtonContainer>
          <Button onClick={handleSubmit} $variant="save">
            Salvar
          </Button>
          <Button onClick={handleClose} $variant="cancel">
            Cancelar
          </Button>
        </ButtonContainer>
      </ModalContent>
    </ModalOverlay>
  );
};

// Styled components here (same as TaskModal)

export default EditTaskModal;
