import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { Link, useNavigate } from 'react-router-dom';
import { FaArrowRight } from 'react-icons/fa';
import { useAuth } from '../hooks/useAuth';
import { useInput } from '../hooks/useInput';

const LoginPage = () => {
  const { login, error, userInfo, isVerificAuth } = useAuth();
  const navigate = useNavigate();
  const { value: email, onChange: handleEmailChange } = useInput('');
  const { value: password, onChange: handlePasswordChange } = useInput('');
  const [showPassword, setShowPassword] = useState(false);
  const [loadingStartTime, setLoadingStartTime] = useState(null);
  const [minLoadingDuration] = useState(5000);
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const validateEmail = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const handleShowPassword = () => {
    if (validateEmail(email)) {
      setShowPassword(true);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoadingStartTime(Date.now());

    try {
      await login(email, password);
      setIsAuthenticated(true); // Define o estado isAuthenticated como true
    } catch (err) {
      console.error('Erro ao fazer login:', err);
    }
  };

  useEffect(() => {
    if (isAuthenticated && userInfo.username) {
      const elapsedTime = Date.now() - loadingStartTime;
      const remainingTime = Math.max(minLoadingDuration - elapsedTime, 0);
      setTimeout(() => {
        navigate(`/tasks/${userInfo.username}`);
      }, remainingTime);
    }
  }, [isAuthenticated, userInfo, navigate, loadingStartTime, minLoadingDuration]);

  return (
    <Container>
      <Form onSubmit={handleSubmit}>
        <Logo
          src="https://firebasestorage.googleapis.com/v0/b/connectrip-10205.appspot.com/o/task%2FConnecter-form-preview.png?alt=media&token=da607aba-6727-4eee-a65a-968c88455272"
          alt="Connecter Intro"
        />
        <Title>Faça login com seu ID Connecter</Title>
        <EmailContainer>
          <Input
            type="email"
            placeholder="E-mail"
            value={email}
            onChange={handleEmailChange}
            autoFocus
          />
          {!showPassword && (
            <Button
              onClick={handleShowPassword}
              disabled={!validateEmail(email)}
              emptyEmail={!email}
            >
              <FaArrowRight />
            </Button>
          )}
        </EmailContainer>
        <PasswordContainer show={showPassword}>
          <Input
            type="password"
            placeholder="Senha"
            value={password}
            onChange={handlePasswordChange}
          />
          <Button type="submit">
            <FaArrowRight />
          </Button>
        </PasswordContainer>
        <RegisterLink to="/register">Criar um ID Connecter</RegisterLink>
        {error && <ErrorMessage>{error}</ErrorMessage>}
      </Form>
    </Container>
  );
};

// (O restante do código de estilização permanece o mesmo)

export default LoginPage;
