import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import styled, { keyframes } from 'styled-components';
import { jwtDecode } from 'jwt-decode';

const capitalizeFirstLetter = (string) => {
  return string.charAt(0).toUpperCase() + string.slice(1);
};

const formatDate = (dateString) => {
  const options = { year: 'numeric', month: 'long', day: 'numeric', hour: '2-digit', minute: '2-digit' };
  return new Date(dateString).toLocaleDateString(undefined, options);
};

const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const TasksPage = () => {
  const { username } = useParams();
  const [jobs, setJobs] = useState([]);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState({ firstName: '', email: '', createdAt: '' });
  const [selectedJob, setSelectedJob] = useState(null);
  const [showLoginModal, setShowLoginModal] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');

    if (token) {
      setIsAuthenticated(true);

      try {
        const decodedToken = jwtDecode(token);
        const fullName = decodedToken.name || 'Nome não disponível';
        const firstName = capitalizeFirstLetter(fullName.split(' ')[0]);

        setUser({
          firstName,
          email: decodedToken.email || 'Email não disponível',
        });

        if (username !== firstName) {
          navigate(`/jobs/${encodeURIComponent(firstName)}`);
        }
      } catch (error) {
        console.error('Erro ao decodificar o token:', error);
      }
    } else {
      const fetchUserInfo = async () => {
        try {
          const response = await fetch(`http://localhost:5000/api/users/user/${encodeURIComponent(username)}`);
          if (response.ok) {
            const data = await response.json();
            setUser({
              firstName: capitalizeFirstLetter(data.name),
              email: data.email,
              createdAt: data.createdAt,
            });
          } else {
            console.error('Erro ao carregar informações do usuário:', response.statusText);
          }
        } catch (error) {
          console.error('Erro ao carregar informações do usuário:', error);
        }
      };

      fetchUserInfo();
    }

    const fetchJobs = async () => {
      try {
        const apiUrl = isAuthenticated 
          ? `http://localhost:5000/api/jobs/` 
          : `http://localhost:5000/api/jobs/user/${encodeURIComponent(username)}`;

        const response = await fetch(apiUrl, {
          headers: isAuthenticated ? { Authorization: `Bearer ${token}` } : {},
        });

        if (response.ok) {
          const data = await response.json();
          setJobs(data);
        } else {
          console.error('Erro ao carregar empregos:', response.statusText);
        }
      } catch (error) {
        console.error('Erro ao carregar empregos:', error);
      }
    };

    fetchJobs();
  }, [navigate, username, isAuthenticated]);

  const handleViewJob = (job) => {
    if (isAuthenticated) {
      setSelectedJob(job);
    } else {
      setShowLoginModal(true);
    }
  };

  const handleCloseModal = () => {
    setSelectedJob(null);
  };

  const handleCloseLoginModal = () => {
    setShowLoginModal(false);
  };

  return (
    <Container>
      <Content>
        <Header>
          <h1>Vagas de Emprego</h1>
        </Header>
        <MainContent>
          <ProfileSection>
            <h2>Informações de {user.firstName}</h2>
            <ProfileInfo>
              <InfoItem>
                <strong>Nome:</strong> {user.firstName}
              </InfoItem>
              <InfoItem>
                <strong>Email:</strong> {user.email}
              </InfoItem>
              <InfoItem>
                <strong>Data de Criação:</strong> {formatDate(user.createdAt)}
              </InfoItem>
            </ProfileInfo>
          </ProfileSection>
          <JobsSection>
            <h2>Empregos Disponíveis</h2>
            <JobsGrid>
              {jobs.slice(0, 6).map((job) => (
                <JobCard key={job.id}>
                  <JobTitle>{job.jobTitle}</JobTitle>
                  <JobDate>{formatDate(job.postedAt)}</JobDate>
                  <ViewButton onClick={() => handleViewJob(job)}>
                    Ver Detalhes
                  </ViewButton>
                </JobCard>
              ))}
            </JobsGrid>
          </JobsSection>
        </MainContent>
      </Content>

      {selectedJob && (
        <Modal>
          <ModalContent>
            <CloseButton onClick={handleCloseModal}>×</CloseButton>
            <ModalHeader>
              <h3>{selectedJob.jobTitle}</h3>
              <ModalDate>{formatDate(selectedJob.postedAt)}</ModalDate>
            </ModalHeader>
            <ModalBody>
              <p><strong>Descrição:</strong> {selectedJob.jobDescription}</p>
              <p><strong>Localização:</strong> {selectedJob.location}</p>
              <p><strong>Tipo:</strong> {selectedJob.jobType}</p>
            </ModalBody>
          </ModalContent>
        </Modal>
      )}

      {showLoginModal && (
        <LoginModal>
          <LoginModalContent>
            <CloseButton onClick={handleCloseLoginModal}>×</CloseButton>
            <h2>Você precisa estar logado para ver mais detalhes</h2>
            <LoginButton onClick={() => navigate('/login')}>Login</LoginButton>
          </LoginModalContent>
        </LoginModal>
      )}
    </Container>
  );
};

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  background-color: #f5f5f5; /* Cor de fundo clara */
  padding: 2rem;
  min-height: 100vh;
`;

const Content = styled.div`
  width: 100%;
  max-width: 1200px;
  margin: 0 auto;
`;

const Header = styled.header`
  text-align: center;
  margin-bottom: 2rem;

  h1 {
    font-size: 2.5rem;
    color: #333; /* Cor escura para o título */
  }
`;

