import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { ThemeProvider } from 'styled-components';
import theme from './styles/theme';
import LoginPage from './pages/LoginPage';
import RegisterPage from './pages/RegisterPage';
import TasksPage from './pages/TasksPage';
import Header from './components/Header';
import Footer from './components/Footer';
import IntroPage from './components/Intro';
import styled from 'styled-components';

function App() {
  const [isHeaderLoaded, setIsHeaderLoaded] = useState(false);

  useEffect(() => {
    // Simula um atraso no carregamento do Header
    const timer = setTimeout(() => setIsHeaderLoaded(true), 100); // Ajuste o tempo conforme necessário
    return () => clearTimeout(timer);
  }, []);

  return (
    <ThemeProvider theme={theme}>
      <Router>
        <AppContainer>
          <Header onLoad={() => setIsHeaderLoaded(true)} />
          {isHeaderLoaded && (
            <>
              <MainContent>
                <Routes>
                  <Route path="/" element={<IntroPage />} />
                  <Route path="/login" element={<LoginPage />} />
                  <Route path="/register" element={<RegisterPage />} />
                  <Route path="/tasks" element={<TasksPage />} />
                </Routes>
              </MainContent>
              <Footer />
            </>
          )}
        </AppContainer>
      </Router>
    </ThemeProvider>
  );
}

const AppContainer = styled.div`
  display: flex;
  flex-direction: column;
  min-height: 100vh; /* Garante que o contêiner ocupe a altura total da viewport */
`;

const MainContent = styled.main`
  flex: 1; /* Permite que o conteúdo principal ocupe o espaço restante */
`;

export default App;
