import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import { FaArrowRight } from 'react-icons/fa';
import { useAuth } from '../hooks/useAuth';
import { useInput } from '../hooks/useInput';
import Loader from '../components/Loader'; // Componente de tela de carregamento

const LoginPage = () => {
  const { login, error } = useAuth();
  const { value: email, onChange: handleEmailChange } = useInput('');
  const { value: password, onChange: handlePasswordChange } = useInput('');
  const [showPassword, setShowPassword] = useState(false);
  const [loading, setLoading] = useState(false); // Estado de carregamento
  const [minLoadingTime, setMinLoadingTime] = useState(0); // Tempo mínimo de carregamento
  const [loadingStartTime, setLoadingStartTime] = useState(null); // Hora de início do carregamento

  const validateEmail = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const handleShowPassword = () => {
    if (validateEmail(email)) {
      setShowPassword(true);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true); // Ativa o carregamento
    setLoadingStartTime(Date.now()); // Registra o tempo de início do carregamento

    // Define o tempo mínimo de carregamento
    const minLoadingDuration = 5000; // 5 segundos
    setMinLoadingTime(minLoadingDuration);

    try {
      await login(email, password);
      // A autenticação foi bem-sucedida, garante que o loader fique visível o tempo mínimo
      const elapsedTime = Date.now() - loadingStartTime;
      const remainingTime = Math.max(minLoadingDuration - elapsedTime, 0);

      // Define o tempo adicional para manter o loader visível se o tempo de carregamento for menor que 5 segundos
      setTimeout(() => {
        setLoading(false);
      }, remainingTime);

      // Faz o reload após o carregamento
      setTimeout(() => {
        window.location.reload(); // Ajuste o tempo conforme necessário para o carregamento
      }, 1000); // Ajuste o tempo conforme necessário
    } catch (err) {
      // Desativa o carregamento se ocorrer um erro
      setLoading(false);
      console.error('Erro ao fazer login:', err);
    }
  };

  if (loading) {
    return <Loader />; // Exibe a tela de carregamento enquanto o login está em processo
  }

  return (
    <Container>
      <Form onSubmit={handleSubmit}>
        <Logo
          src="https://firebasestorage.googleapis.com/v0/b/connectrip-10205.appspot.com/o/task%2FConnecter-form-preview.png?alt=media&token=da607aba-6727-4eee-a65a-968c88455272"
          alt="Connecter Intro"
        />
        <Title>Faça login com seu ID Connecter</Title>
        <EmailContainer>
          <Input
            type="email"
            placeholder="E-mail"
            value={email}
            onChange={handleEmailChange}
            autoFocus
          />
          {!showPassword && (
            <Button
              onClick={handleShowPassword}
              disabled={!validateEmail(email)}
              emptyEmail={!email}
            >
              <FaArrowRight />
            </Button>
          )}
        </EmailContainer>
        {showPassword && (
          <PasswordContainer>
            <Input
              type="password"
              placeholder="Senha"
              value={password}
              onChange={handlePasswordChange}
            />
            <Button type="submit">
              <FaArrowRight />
            </Button>
          </PasswordContainer>
        )}
        <RegisterLink to="/register">Criar um ID Connecter</RegisterLink>
        {error && <ErrorMessage>{error}</ErrorMessage>}
      </Form>
    </Container>
  );
};

const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 60vh;
  margin-top: 7rem; /* Ajuste para trazer a imagem mais para cima */
`;

const Form = styled.form`
  padding: 2rem;
  max-width: 560px;
  width: 100%;
  text-align: center;
  background: white;
  border-radius: 30px;
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.4);
`;

const Logo = styled.img`
  width: 200px; /* Ajuste o tamanho conforme necessário */
  height: 190px;
  margin-bottom: -1rem; /* Diminuído o espaço entre o logo e o título */
`;

const Title = styled.h2`
  margin-bottom: 2rem;
  font-size: 1.5rem;
  font-weight: 500;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto,
    'Helvetica Neue', Arial, sans-serif;
`;

const EmailContainer = styled.div`
  position: relative;
  width: 100%;
  margin-bottom: 1rem;
`;

const PasswordContainer = styled.div`
  position: relative;
  width: 100%;
`;

const Input = styled.input`
  width: 70%;
  padding: 1.2rem;
  padding-right: 3.5rem; /* Aumenta o padding para criar mais espaço para o botão */
  border: 1px solid #a4a4ad;
  border-radius: 12px;
  font-size: 1rem;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto,
    'Helvetica Neue', Arial, sans-serif;
  transition:
    border-color 0.3s,
    box-shadow 0.3s;

  &:focus {
    border-color: #007aff;
    box-shadow: 0 0 0 2px rgba(0, 122, 255, 0.2);
    outline: none;
  }
`;

const Button = styled.button`
  position: absolute;
  top: 50%;
  right: 3.6rem; /* Ajusta a posição do botão */
  transform: translateY(-50%);
  width: 2.2rem; /* Ajustar para o tamanho desejado */
  height: 2.2rem;
  background: ${(props) =>
    props.emptyEmail
      ? '#d0d0d5'
      : '#007aff'}; /* Cor diferente quando o email está vazio */
  border: none;
  color: #ffffff;
  border-radius: 50%;
  font-size: 1rem; /* Ajustar tamanho do ícone */
  font-weight: bold; /* Deixa a seta em negrito */
  cursor: ${(props) =>
    props.emptyEmail
      ? 'default'
      : 'pointer'}; /* Desabilita o cursor pointer se o email estiver vazio */
  padding: 0;
  display: flex;
  align-items: center;
  justify-content: center;

  &:hover {
    background: ${(props) =>
      props.emptyEmail
        ? '#d0d0d5'
        : '#0051a8'}; /* Cor diferente quando o email está vazio */
  }

  &:disabled {
    cursor: not-allowed;
    opacity: 0.5;
  }
`;

const RegisterLink = styled(Link)`
  display: block;
  margin-top: 1.5rem;
  color: #007aff;
  font-size: 1rem;
  text-decoration: none;
`;

const ErrorMessage = styled.div`
  color: red;
  margin-top: 1rem;
`;

export default LoginPage;
