// src/components/TaskModal.jsx
import React from 'react';
import { ModalContainer, ModalContent, CloseButton, ModalHeader, ModalBody, ModalDate } from '../styles/TaskModal.styles';

const TaskModal = ({ selectedTask, onClose }) => (
  <ModalContainer>
    <ModalContent>
      <CloseButton onClick={onClose}>×</CloseButton>
      <ModalHeader>
        <h3>{selectedTask.taskTitle}</h3>
        <ModalDate>{selectedTask.createdAt}</ModalDate>
      </ModalHeader>
      <ModalBody>
        <p><strong>Descrição:</strong> {selectedTask.taskDescription}</p>
        <p><strong>Status:</strong> {selectedTask.taskStatus ? 'Concluído' : 'Pendente'}</p>
      </ModalBody>
    </ModalContent>
  </ModalContainer>
);

export default TaskModal;
