import React from 'react';
import styled from 'styled-components';
import { FaTasks } from 'react-icons/fa';
import useClickOutside from '../../hooks/useClickOutside'; // Importar o hook customizado

const TaskMenu = ({ onViewTasks }) => {
  const ref = useClickOutside(() => {
    // Função para fechar o menu quando clicar fora, sem chamar onViewTasks
    // Você pode definir uma função para fechar o menu se necessário
  });

  return (
    <TaskMenuContainer ref={ref}>
      <TaskLink onClick={() => {
        // Chama onViewTasks para gerenciar tarefas
        onViewTasks();
      }}>
        <TaskIcon />
        Gerenciar Tarefas
      </TaskLink>
    </TaskMenuContainer>
  );
};

const TaskMenuContainer = styled.div`
  position: absolute;
  top: 100%;
  right: 0;
  background-color: #1e1e1e;
  border-radius: 10px;
  box-shadow: 0 6px 16px rgba(0, 0, 0, 0.4);
  padding: 0.5rem;
  display: flex;
  flex-direction: column;
  align-items: stretch;
  z-index: 1000;
  min-width: 200px;
`;

const TaskLink = styled.div`
  color: #f0f0f0;
  text-decoration: none;
  padding: 0.75rem 1rem;
  cursor: pointer;
  font-size: 1rem;
  transition: background-color 0.3s ease, transform 0.3s ease;
  border-radius: 8px;
  display: flex;
  align-items: center;

  &:hover {
    background-color: #2c2c2c;
    transform: translateX(8px);
  }

  &:focus {
    outline: 3px solid #1e90ff;
    outline-offset: 4px;
  }
`;

const TaskIcon = styled(FaTasks)`
  margin-right: 0.5rem;
  color: #ffdd57;
`;

export default TaskMenu;
