// Intro.js
import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import { FaTasks, FaChartLine, FaRegLightbulb } from 'react-icons/fa'; // Ícones da biblioteca react-icons

const Intro = () => {
  return (
    <IntroContainer>
      <Logo src="https://firebasestorage.googleapis.com/v0/b/connectrip-10205.appspot.com/o/task%2FConnecter-intro.gif?alt=media&token=76402d79-bb17-45e7-a831-5a1fd29818ab" alt="Connecter Intro" />
      <Title>Connecter</Title>
      <LoginButton to="/login">Iniciar Sessão</LoginButton>
      <IntroText>
        Transforme suas tarefas em conquistas com uma gestão simples e eficiente.
      </IntroText>
      <FeaturesContainer>
        <Feature>
          <FeatureIcon><FaTasks /></FeatureIcon>
          <FeatureTitle>Crie Tarefas</FeatureTitle>
          <FeatureDescription>Adicione e organize suas tarefas de forma simples e rápida.</FeatureDescription>
        </Feature>
        <Feature>
          <FeatureIcon><FaChartLine /></FeatureIcon>
          <FeatureTitle>Monitore a Situação</FeatureTitle>
          <FeatureDescription>Veja o progresso e mantenha-se no controle das suas atividades.</FeatureDescription>
        </Feature>
        <Feature>
          <FeatureIcon><FaRegLightbulb /></FeatureIcon>
          <FeatureTitle>Fácil e Prático</FeatureTitle>
          <FeatureDescription>Uma interface intuitiva que torna a gestão das tarefas mais fácil do que nunca.</FeatureDescription>
        </Feature>
      </FeaturesContainer>
      <CTASection>
        <h2>Pronto para começar?</h2>
        <p>Descubra como a Connecter pode transformar a forma como você gerencia suas tarefas.</p>
        <CTAButton to="/signup">Crie sua conta agora</CTAButton>
      </CTASection>
      <TestimonialsContainer>
        <h2>O que nossos usuários dizem</h2>
        <Testimonial>
          <TestimonialText>"A Connecter mudou a forma como eu gerencio meu tempo e tarefas. Simples e eficaz!"</TestimonialText>
          <TestimonialAuthor>João Silva</TestimonialAuthor>
        </Testimonial>
        <Testimonial>
          <TestimonialText>"A interface é intuitiva e a gestão de tarefas nunca foi tão fácil. Recomendo!"</TestimonialText>
          <TestimonialAuthor>Maria Oliveira</TestimonialAuthor>
        </Testimonial>
      </TestimonialsContainer>
      <FAQContainer>
        <h2>Perguntas Frequentes</h2>
        <FAQItem>
          <FAQQuestion>Como posso criar uma nova tarefa?</FAQQuestion>
          <FAQAnswer>Para criar uma nova tarefa, clique no botão "Adicionar Tarefa" e preencha os detalhes.</FAQAnswer>
        </FAQItem>
        <FAQItem>
          <FAQQuestion>Posso monitorar o progresso das minhas tarefas?</FAQQuestion>
          <FAQAnswer>Sim, você pode acompanhar o progresso de cada tarefa na seção "Meus Tarefas".</FAQAnswer>
        </FAQItem>
        {/* Adicione mais perguntas e respostas conforme necessário */}
      </FAQContainer>
    </IntroContainer>
  );
};

const IntroContainer = styled.div`
  text-align: center;
  padding: 2rem;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
  color: #fff; /* Cor do texto branco para contraste */
  background-color: #000; /* Fundo preto */
`;

const Logo = styled.img`
  width: 350px;
  height: 350px;
  margin-bottom: -2rem;
  margin-top: 3rem;
  animation: bounce 2s infinite; /* Animação de salto */
`;

const Title = styled.h1`
  font-size: 6rem;
  color: #fff;
  font-weight: bold;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif;
  margin-bottom: 1.7rem;
  animation: fadeIn 2s ease-in-out; /* Animação de entrada */
`;

const IntroText = styled.p`
  font-size: 2rem;
  color: #ccc;
  font-weight: bold;
  margin: 1rem auto;
  max-width: 600px;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif;
  animation: fadeIn 2s ease-in-out;
`;

const LoginButton = styled(Link)`
  background-color: #fff; /* Cor do botão branco */
  color: #000;
  border: none;
  padding: 0.75rem 2rem;
  border-radius: 20px;
  font-size: 1.1rem;
  font-weight: 600;
  cursor: pointer;
  margin-bottom: 1.7rem;
  transition: background-color 0.3s, transform 0.3s; /* Adiciona transição ao botão */
  text-decoration: none;
  animation: pulse 1.5s infinite; /* Animação de pulso */

  &:hover {
    color: #fff;
    background-color: #333; /* Cor do botão ao passar o mouse */
    transform: scale(1.05); /* Levemente maior ao passar o mouse */
  }
`;

const FeaturesContainer = styled.div`
  display: flex;
  flex-wrap: wrap; /* Permite que os widgets se ajustem à tela */
  justify-content: center;
  margin-top: 2rem;
  gap: 1rem; /* Espaçamento entre os cards */
`;

const Feature = styled.div`
  background: linear-gradient(135deg, #fff, #f0f0f0); /* Gradiente sutil de branco */
  border: 1px solid #ccc; /* Borda fina e sutil */
  border-radius: 10px;
  padding: 2rem;
  margin: 1rem;
  width: 250px;
  box-shadow: 0 8px 16px rgba(0, 0, 0, 0.3); /* Sombra mais pronunciada */
  color: #000;
  transition: transform 0.3s, box-shadow 0.3s, background-color 0.3s; /* Transições para animação */

  &:hover {
    transform: translateY(-10px); /* Levanta o card ao passar o mouse */
    box-shadow: 0 12px 24px rgba(0, 0, 0, 0.4); /* Aumenta a sombra ao passar o mouse */
    background-color: #fafafa; /* Cor de fundo mais clara ao passar o mouse */
  }
`;

const FeatureIcon = styled.div`
  font-size: 3rem; /* Tamanho do ícone */
  color: #000;
  margin-bottom: 1rem;
`;

const FeatureTitle = styled.h3`
  font-size: 1.5rem;
  color: #333;
  margin-bottom: 0.5rem;
  font-weight: 600;
`;

const FeatureDescription = styled.p`
  font-size: 1rem;
  color: #666; /* Cor de texto mais suave */
`;

const CTASection = styled.div`
  margin-top: 2rem;
  padding: 2rem;
  background-color: #333; /* Fundo escuro */
  color: #fff;
  border-radius: 10px;
  text-align: center;
`;

const CTAButton = styled(Link)`
  background-color: #fff;
  color: #000;
  border: none;
