// Intro.js
import React from 'react';
import styled, { keyframes } from 'styled-components';
import { Link } from 'react-router-dom';

// Animação de entrada para os cards
const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const Intro = () => {
  return (
    <IntroContainer>
      <ContentWrapper>
        <Title>Connecter</Title>
        <IntroText>O melhor lugar para gerenciar suas tarefas e muito mais.</IntroText>
        <CardsContainer>
          <Card>
            <iPhoneImage src="https://via.placeholder.com/200x400?text=Task+List" alt="Task List" />
            <CardTitle>Lista de Tarefas</CardTitle>
          </Card>
          <Card>
            <iPhoneImage src="https://via.placeholder.com/200x400?text=Calendar" alt="Calendar" />
            <CardTitle>Calendário</CardTitle>
          </Card>
          <Card>
            <iPhoneImage src="https://via.placeholder.com/200x400?text=Notifications" alt="Notifications" />
            <CardTitle>Notificações</CardTitle>
          </Card>
        </CardsContainer>
        <LoginButton to="/login">Iniciar Sessão</LoginButton>
      </ContentWrapper>
    </IntroContainer>
  );
};

const IntroContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  text-align: center;
  padding: 2rem;
  background: #000; /* Fundo preto */
  min-height: 100vh;
`;

const ContentWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  animation: ${fadeIn} 1s ease-in-out forwards;
  z-index: 1;
`;

const Title = styled.h1`
  font-size: 5rem;
  color: #fff;
  font-weight: bold;
  margin-bottom: 1.5rem;
  font-family: 'Raleway', sans-serif;
`;

const IntroText = styled.p`
  font-size: 1.5rem;
  color: #ddd;
  margin-bottom: 2rem;
  max-width: 500px;
  font-family: 'Raleway', sans-serif;
`;

const CardsContainer = styled.div`
  display: flex;
  justify-content: center;
  gap: 2rem;
  margin-bottom: 3rem;
  flex-wrap: wrap;
`;

const Card = styled.div`
  background: #fff;
  border-radius: 20px;
  padding: 1rem;
  width: 220px;
  text-align: center;
  transition: transform 0.3s, box-shadow 0.3s;
  animation: ${fadeIn} 1.5s ease-in-out forwards;
  cursor: pointer;

  &:hover {
    transform: scale(1.05);
    box-shadow: 0 10px 20px rgba(0, 0, 0, 0.2);
  }
`;

const iPhoneImage = styled.img`
  width: 100%;
  border-radius: 10px;
  margin-bottom: 1rem;
`;

const CardTitle = styled.h2`
  font-size: 1.2rem;
  color: #000;
  font-weight: bold;
`;

const LoginButton = styled(Link)`
  background-color: #fff;
  color: #000;
  border: none;
  padding: 1rem 3rem;
  border-radius: 30px;
  font-size: 1.2rem;
  font-weight: 700;
  cursor: pointer;
  transition: transform 0.3s, background-color 0.3s;
  text-decoration: none;

  &:hover {
    transform: scale(1.05);
    background-color: #f0f0f0;
  }

  &:active {
    transform: scale(0.95);
  }
`;

export default Intro;
