import React, { useState, useEffect, useRef } from 'react';
import { FaEllipsisV } from 'react-icons/fa';
import styled from 'styled-components';

const DropdownMenu = ({ onEdit, onDelete, onComplete, isCompleted }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [hoveredItem, setHoveredItem] = useState(null);
  const menuRef = useRef(null);

  const handleToggle = () => setIsOpen(!isOpen);

  const handleClickOutside = (event) => {
    if (menuRef.current && !menuRef.current.contains(event.target)) {
      setIsOpen(false);
    }
  };

  useEffect(() => {
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  return (
    <MenuContainer ref={menuRef}>
      <Icon onClick={handleToggle} />
      {isOpen && (
        <Dropdown>
          <MenuItem
            onClick={() => { onComplete(); setIsOpen(false); }}
            isHovered={hoveredItem === 'complete'}
            onMouseEnter={() => setHoveredItem('complete')}
            onMouseLeave={() => setHoveredItem(null)}
          >
            {isCompleted ? 'Marcar como Concluída' : 'Concluir'}
          </MenuItem>
          <MenuItem
            onClick={() => { onEdit(); setIsOpen(false); }}
            isHovered={hoveredItem === 'edit'}
            onMouseEnter={() => setHoveredItem('edit')}
            onMouseLeave={() => setHoveredItem(null)}
          >
            Editar
          </MenuItem>
          <MenuItem
            onClick={() => { onDelete(); setIsOpen(false); }}
            isHovered={hoveredItem === 'delete'}
            onMouseEnter={() => setHoveredItem('delete')}
            onMouseLeave={() => setHoveredItem(null)}
          >
            Excluir
          </MenuItem>
        </Dropdown>
      )}
    </MenuContainer>
  );
};

const MenuContainer = styled.div`
  position: relative;
  display: flex;
  flex-direction: column;
  align-items: flex-end;
`;

const Icon = styled(FaEllipsisV)`
  font-size: 22px;
  cursor: pointer;
  color: #ea4f97; /* Cor primária para o ícone */
  transform: rotate(90deg); /* Rotaciona o ícone em 90 graus */
  transition: color 0.3s ease;

  &:hover {
    color: #f291be; /* Cor secundária ao passar o mouse */
  }
`;

const Dropdown = styled.div`
  position: absolute;
  top: 30px;
  right: 0;
  background-color: #1e1e1e; /* Cor de fundo para o menu */
  border-radius: 8px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.3);
  min-width: 120px;
  z-index: 1000;
  overflow: hidden;

  @media (max-width: 768px) {
    min-width: 100px; /* Ajusta a largura em telas menores */
  }
`;

const MenuItem = styled.button`
  display: block;
  padding: 12px 15px;
  color: #f5f5f5; /* Cor do texto dos itens do menu */
  background-color: ${({ isHovered }) => (isHovered ? '#333' : '#1e1e1e')}; /* Cor de fundo para o item do menu */
  border: none;
  text-align: left;
  cursor: pointer;
  width: 100%;
  border-bottom: 1px solid #333;
  transition: background-color 0.3s;
  font-size: 1rem;

  &:last-child {
    border-bottom: none;
  }

  &:hover {
    background-color: #333; /* Cor cinza claro para o hover */
  }

  @media (max-width: 768px) {
    font-size: 0.9rem;
    padding: 10px 12px;
  }
`;

export default DropdownMenu;
