// useTasks.js
import { useState, useEffect, useRef } from 'react';
import { fetchTasks, completeTask, updateTask, deleteTask, createTask } from './api';
import { jwtDecode } from 'jwt-decode';
import { useNavigate } from 'react-router-dom';

export const useTasks = () => {
  const [tasks, setTasks] = useState([]);
  const [filteredTasks, setFilteredTasks] = useState([]);
  const [filter, setFilter] = useState('recent');
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState({ firstName: '', email: '' });
  const [selectedTask, setSelectedTask] = useState(null);
  const [modalOpen, setModalOpen] = useState(false);
  const [menuOpen, setMenuOpen] = useState(null);
  const menuRef = useRef(null);
  const [newTaskTitle, setNewTaskTitle] = useState('');
  const [newTaskDescription, setNewTaskDescription] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('token');

    if (token) {
      setIsAuthenticated(true);

      try {
        const decodedToken = jwtDecode(token);
        const fullName = decodedToken.name || 'Nome não disponível';
        const firstName = capitalizeFirstLetter(fullName.split(' ')[0]);

        setUser({
          firstName,
          email: decodedToken.email || 'Email não disponível',
        });

        const loadTasks = async () => {
          try {
            const data = await fetchTasks();
            setTasks(data);
            filterTasks(data, filter);
          } catch (error) {
            console.error('Erro ao carregar tarefas:', error);
          }
        };

        loadTasks();
      } catch (error) {
        console.error('Erro ao decodificar o token:', error);
      }
    } else {
      navigate('/login');
    }
  }, [navigate, filter]);

  useEffect(() => {
    if (tasks.length) {
      filterTasks(tasks, filter);
    }
  }, [tasks, filter]);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setMenuOpen(null); // Fecha o menu ao clicar fora
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const capitalizeFirstLetter = (string) => {
    return string.charAt(0).toUpperCase() + string.slice(1);
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'Data não disponível.';
    const options = { year: 'numeric', month: 'long', day: 'numeric', hour: '2-digit', minute: '2-digit' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  const handleFilterChange = (newFilter) => {
    setFilter(newFilter);
    filterTasks(tasks, newFilter);
  };

  const filterTasks = (tasks, filter) => {
    switch (filter) {
      case 'recent':
        setFilteredTasks([...tasks].filter(task => !task.taskStatus).sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt)));
        break;
      case 'completed':
        setFilteredTasks(tasks.filter(task => task.taskStatus));
        break;
      case 'pending':
        setFilteredTasks(tasks.filter(task => !task.taskStatus));
        break;
      case 'all':
        setFilteredTasks(tasks);
        break;
      default:
        setFilteredTasks(tasks);
    }
  };

  const handleCompleteTask = async (taskId) => {
    try {
      await completeTask(taskId);
      const updatedTasks = tasks.map(task =>
        task.id === taskId ? { ...task, taskStatus: true } : task
      );
      setTasks(updatedTasks);
      filterTasks(updatedTasks, filter);
      handleCloseModal();
    } catch (error) {
      console.error('Erro ao marcar tarefa como concluída:', error);
    }
  };

  const handleEditClick = (task) => {
    setSelectedTask(task);
    setModalOpen(true);
    setMenuOpen(null); // Fecha o menu ao clicar em editar
  };

  const handleCloseModal = () => {
    setModalOpen(false);
    setSelectedTask(null);
  };

  const handleUpdateTask = async (updatedTask) => {
    try {
      const updatedTaskData = await updateTask(updatedTask);
      setTasks(prevTasks => {
        const updatedTasks = prevTasks.map(task =>
          task.id === updatedTaskData.id ? updatedTaskData : task
        );
        filterTasks(updatedTasks, filter);
        return updatedTasks;
      });
      handleCloseModal();
    } catch (error) {
      console.error('Erro ao atualizar tarefa:', error);
    }
  };

  const handleDeleteTask = async (taskId) => {
    try {
      await deleteTask(taskId);
      setTasks(prevTasks => {
        const updatedTasks = prevTasks.filter(task => task.id !== taskId);
        filterTasks(updatedTasks, filter);
        return updatedTasks;
      });
      handleCloseModal();
    } catch (error) {
      console.error('Erro ao excluir tarefa:', error);
    }
  };

  const handleAddTask = async () => {
    if (!newTaskTitle.trim()) {
      alert("O título da tarefa não pode estar vazio.");
      return;
    }

    const newTask = {
      title: newTaskTitle,
      description: newTaskDescription,
    };

    try {
      const { userTask } = await createTask(newTask);
      setTasks(prevTasks => [userTask, ...prevTasks]);
      filterTasks([userTask, ...tasks], filter);
      handleCloseModal();
    } catch (error) {
      console.error('Erro ao criar tarefa:', error);
    }
  };

  return {
    tasks,
    filteredTasks,
    filter,
    isAuthenticated,
    user,
    selectedTask,
    modalOpen,
    menuOpen,
    menuRef,
    newTaskTitle,
    newTaskDescription,
    setNewTaskTitle,
    setNewTaskDescription,
    handleFilterChange,
    handleCompleteTask,
    handleEditClick,
    handleCloseModal,
    handleUpdateTask,
    handleDeleteTask,
    handleAddTask,
  };
};
