// Intro.js
import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';

const Intro = () => {
  return (
    <IntroContainer>
      <VideoContainer>
        <Video
          src="https://videocdn.cdnpk.net/videos/ac17c997-e18e-4790-841c-c287326ddb60/horizontal/previews/clear/large.mp4?token=exp=1725682391~hmac=1d9b4ec76ad7a557222db26665dbf7478f118475668b52bf9cf6ca59b4f46c2c"
          autoPlay
          loop
          muted
          playsInline
        />
      </VideoContainer>
      <Title>Connecter</Title>
      <LoginButton to="/login">Iniciar Sessão</LoginButton>
      <IntroText>
        O melhor lugar para gerenciar suas tarefas e muito mais.
      </IntroText>
    </IntroContainer>
  );
};

const IntroContainer = styled.div`
  text-align: center;
  padding: 2rem;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
  overflow: hidden; /* Garante que nada saia da área visível */
`;

const VideoContainer = styled.div`
  width: 100%; /* Largura total da página */
  height: 60vh; /* Altura do vídeo, ajuste conforme necessário */
  overflow: hidden;
  position: relative;
  margin-bottom: -2rem;
  margin-top: 3rem;
`;

const Video = styled.video`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  object-fit: cover; /* Ajusta o vídeo para cobrir a área sem distorcer */
`;

const Title = styled.h1`
  font-size: 6rem;
  color: #000;
  font-weight: bold;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto,
    'Helvetica Neue', Arial, sans-serif;
  margin-bottom: 1.7rem;
`;

const IntroText = styled.p`
  font-size: 2rem;
  color: #000;
  font-weight: bold;
  margin: 1rem auto;
  max-width: 500px;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto,
    'Helvetica Neue', Arial, sans-serif;
`;

const LoginButton = styled(Link)`
  background-color: #000;
  color: #fff;
  border: none;
  padding: 0.75rem 2rem;
  border-radius: 20px;
  font-size: 1.1rem;
  font-weight: 600;
  cursor: pointer;
  margin-bottom: 1.7rem;
  transition: background-color 0.3s;
  text-decoration: none;

  &:hover {
    color: #000;
    background-color: gray;
  }
`;

export default Intro;
