import React, { useEffect, useState, useRef } from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';
import { FaEllipsisV, FaCheck } from 'react-icons/fa';
import { useAuth } from '../hooks/useAuth';

const UserTasksPage = () => {
  const [tasks, setTasks] = useState([]);
  const [filteredTasks, setFilteredTasks] = useState([]);
  const [filter, setFilter] = useState('recent');
  const [selectedTask, setSelectedTask] = useState(null);
  const [modalOpen, setModalOpen] = useState(false);
  const [menuOpen, setMenuOpen] = useState(null);
  const menuRef = useRef(null);
  const [newTaskTitle, setNewTaskTitle] = useState('');
  const [newTaskDescription, setNewTaskDescription] = useState('');

  const { token, userInfo, logout } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    if (token) {
      const fetchTasks = async () => {
        try {
          const response = await fetch('http://localhost:5000/api/tasks/', {
            headers: { Authorization: `Bearer ${token}` },
          });

          if (response.ok) {
            const data = await response.json();
            setTasks(data);
            filterTasks(data, filter);
          } else {
            console.error('Erro ao carregar tarefas:', response.statusText);
          }
        } catch (error) {
          console.error('Erro ao carregar tarefas:', error);
        }
      };

      fetchTasks();
    } else {
      navigate('/login');
    }
  }, [navigate, token, filter]);

  useEffect(() => {
    if (tasks.length) {
      filterTasks(tasks, filter);
    }
  }, [tasks, filter]);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setMenuOpen(null); // Fecha o menu ao clicar fora
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const capitalizeFirstLetter = (string) => {
    return string.charAt(0).toUpperCase() + string.slice(1);
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'Data não disponível.';
    const options = { year: 'numeric', month: 'long', day: 'numeric', hour: '2-digit', minute: '2-digit' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  const handleFilterChange = (newFilter) => {
    setFilter(newFilter);
    filterTasks(tasks, newFilter);
  };

  const filterTasks = (tasks, filter) => {
    switch (filter) {
      case 'recent':
        setFilteredTasks([...tasks].filter(task => !task.taskStatus).sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt)));
        break;
      case 'completed':
        setFilteredTasks(tasks.filter(task => task.taskStatus));
        break;
      case 'pending':
        setFilteredTasks(tasks.filter(task => !task.taskStatus));
        break;
      case 'all':
        setFilteredTasks(tasks);
        break;
      default:
        setFilteredTasks(tasks);
    }
  };

  const handleCompleteTask = async (taskId) => {
    try {
      const response = await fetch(`http://localhost:5000/api/tasks/${taskId}/complete`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
      });

      if (response.ok) {
        const updatedTasks = tasks.map(task =>
          task.id === taskId ? { ...task, taskStatus: true } : task
        );
        setTasks(updatedTasks);
        filterTasks(updatedTasks, filter);
        handleCloseModal();
      } else {
        console.error('Erro ao marcar tarefa como concluída:', response.statusText);
      }
    } catch (error) {
      console.error('Erro ao marcar tarefa como concluída:', error);
    }
  };

  const handleEditClick = (task) => {
    setSelectedTask(task);
    setModalOpen(true);
    setMenuOpen(null); // Fecha o menu ao clicar em editar
  };

  const handleCloseModal = () => {
    setModalOpen(false);
    setSelectedTask(null);
  };

  const handleUpdateTask = async (updatedTask) => {
    try {
      const response = await fetch(`http://localhost:5000/api/tasks/${updatedTask.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(updatedTask),
      });

      if (response.ok) {
        const updatedTaskData = await response.json();
        setTasks(prevTasks => {
          const updatedTasks = prevTasks.map(task =>
            task.id === updatedTaskData.id ? updatedTaskData : task
          );
          filterTasks(updatedTasks, filter);
          return updatedTasks;
        });
        handleCloseModal();
      } else {
        console.error('Erro ao atualizar tarefa:', response.statusText);
      }
    } catch (error) {
      console.error('Erro ao atualizar tarefa:', error);
    }
  };

  const handleDeleteTask = async (taskId) => {
    try {
      const response = await fetch(`http://localhost:5000/api/tasks/${taskId}`, {
        method: 'DELETE',
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (response.ok) {
        setTasks(prevTasks => {
          const updatedTasks = prevTasks.filter(task => task.id !== taskId);
          filterTasks(updatedTasks, filter);
          return updatedTasks;
        });
        handleCloseModal();
      } else {
        console.error('Erro ao excluir tarefa:', response.statusText);
      }
    } catch (error) {
      console.error('Erro ao excluir tarefa:', error);
    }
  };

  const handleCreateTask = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/tasks/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          title: newTaskTitle,
          description: newTaskDescription,
          createdBy: userInfo.username, // Use username do userInfo
          createdAt: new Date().toISOString(),
          taskStatus: false,
        }),
      });

      if (response.ok) {
        const newTask = await response.json();
        setTasks(prevTasks => [...prevTasks, newTask]);
        filterTasks([...tasks, newTask], filter);
        setNewTaskTitle('');
        setNewTaskDescription('');
      } else {
        console.error('Erro ao criar tarefa:', response.statusText);
      }
    } catch (error) {
      console.error('Erro ao criar tarefa:', error);
    }
  };

  return (
    <Container>
      <Header>
        <h1>Bem-vindo, {userInfo.username}!</h1>
        <LogoutButton onClick={logout}>Sair</LogoutButton>
      </Header>
      <Main>
        <FilterButtons>
          <Button onClick={() => handleFilterChange('recent')}>Recentes</Button>
          <Button onClick={() => handleFilterChange('completed')}>Concluídas</Button>
          <Button onClick={() => handleFilterChange('pending')}>Pendentes</Button>
          <Button onClick={() => handleFilterChange('all')}>Todas</Button>
        </FilterButtons>
        <TaskList>
          {filteredTasks.map((task) => (
            <TaskCard key={task.id}>
              <TaskContent>
                <h3>{task.title}</h3>
                <p>{task.description}</p>
                <p>{formatDate(task.createdAt)}</p>
              </TaskContent>
              <TaskActions>
                {!task.taskStatus && (
                  <ActionButton onClick={() => handleCompleteTask(task.id)}>
                    <FaCheck /> Concluir
                  </ActionButton>
                )}
                <ActionButton ref={menuRef} onClick={() => setMenuOpen(task.id)}>
                  <FaEllipsisV />
                  {menuOpen === task.id && (
                    <Menu>
                      <MenuItem onClick={() => handleEditClick(task)}>Editar</MenuItem>
                      <MenuItem onClick={() => handleDeleteTask(task.id)}>Excluir</MenuItem>
                    </Menu>
                  )}
                </ActionButton>
              </TaskActions>
            </TaskCard>
          ))}
        </TaskList>
        <NewTask>
          <h2>Criar Nova Tarefa</h2>
          <input
            type="text"
            placeholder="Título"
            value={newTaskTitle}
            onChange={(e) => setNewTaskTitle(e.target.value)}
          />
          <textarea
            placeholder="Descrição"
            value={newTaskDescription}
            onChange={(e) => setNewTaskDescription(e.target.value)}
          />
          <Button onClick={handleCreateTask}>Criar Tarefa</Button>
        </NewTask>
      </Main>
      {modalOpen && selectedTask && (
        <Modal>
          <h2>Editar Tarefa</h2>
          <input
            type="text"
            value={selectedTask.title}
            onChange={(e) => setSelectedTask({ ...selectedTask, title: e.target.value })}
          />
          <textarea
            value={selectedTask.description}
            onChange={(e) => setSelectedTask({ ...selectedTask, description: e.target.value })}
          />
          <Button onClick={() => handleUpdateTask(selectedTask)}>Salvar</Button>
          <Button onClick={handleCloseModal}>Fechar</Button>
        </Modal>
      )}
    </Container>
  );
};

