import { useState, useEffect } from 'react';
import { useAuth } from '../../Context/AuthContext';

const useTasks = (username) => {
  const [tasks, setTasks] = useState([]);
  const [error, setError] = useState(null);
  const { token, userInfo } = useAuth();

  uuseEffect(() => {
    if (!username) {
      setError('Username não fornecido.');
      return;
    }

    const fetchTasks = async () => {
      try {
        let apiUrl;

        if (token) {
          apiUrl = `http://localhost:5000/api/manager/tasks/${encodeURIComponent(username)}`;
        } else {
          apiUrl = `http://localhost:5000/api/manager/public/tasks/${encodeURIComponent(username)}`;
        }

        const response = await fetch(apiUrl, {
          headers: token ? { Authorization: `Bearer ${token}` } : {},
        });

        if (response.ok) {
          const data = await response.json();
          console.log('Dados recebidos:', data); // Adicione este log
          const sortedTasks = data.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
          const recentTasks = sortedTasks.slice(0, 9);
          setTasks(recentTasks);
        } else {
          setError('Erro ao carregar tarefas');
        }
      } catch (error) {
        setError(error.message || 'Erro ao carregar tarefas');
      }
    };

    fetchTasks();
  }, [username, token, userInfo.username]);


  return { tasks, error };
};

export default useTasks;
