import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import styled from 'styled-components';
import useUserInfo from '../hooks/Tasks/useUserInfo';
import useTasks from '../hooks/Tasks/useTasks';
import TaskCard from '../components/Tasks Home/TaskCard';
import Modal from '../components/Tasks Home/Modal';
import UserDetails from '../components/Tasks Home/userDetails';

const TasksPage = () => {
  const { username } = useParams();
  const [selectedTask, setSelectedTask] = useState(null);

  // Obtendo o primeiro nome e o nome completo do usuário
  const { firstName, fullName, email, createdAt } = useUserInfo(username);
  const tasks = useTasks(username);

  const handleViewTask = (task) => setSelectedTask(task);
  const handleCloseModal = () => setSelectedTask(null);

  return (
    <PageContainer>
      <Header>
        <h1>Dashboard de {firstName}</h1> {/* Exibindo apenas o primeiro nome */}
        <p>Visualize suas tarefas e detalhes do usuário</p>
      </Header>
      <Content>
        <UserDetails 
          fullName={fullName} 
          email={email} 
          createdAt={createdAt} 
          username={username} 
        />
        <TasksSection>
          <h2>Suas Tarefas</h2>
          {tasks.length > 0 ? (
            <TasksList>
              {tasks.map((task) => (
                <TaskCard
                  key={task.id}
                  task={task}
                  onViewTask={() => handleViewTask(task)}
                />
              ))}
            </TasksList>
          ) : (
            <NoTasksMessage>Não há tarefas disponíveis.</NoTasksMessage>
          )}
        </TasksSection>
      </Content>
      {selectedTask && (
        <Modal task={selectedTask} onClose={handleCloseModal} />
      )}
    </PageContainer>
  );
};

const PageContainer = styled.div`
  color: #f5f5f5;
  padding: 20px;
  min-height: 100vh;

  @media (max-width: 768px) {
    padding: 15px;
  }
`;

const Header = styled.header`
  text-align: center;
  margin-bottom: 30px;
  margin-top: 5rem;

  h1 {
    font-size: 2.5rem;
    margin-bottom: 10px;
  }

  p {
    color: #bbb;
  }

  @media (max-width: 768px) {
    h1 {
      font-size: 2rem;
    }
  }
`;

const Content = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;

  @media (min-width: 768px) {
    flex-direction: row;
  }
`;

const TasksSection = styled.section`
  flex: 2;
  background-color: #222;
  padding: 20px;
  border-radius: 8px;

  h2 {
    margin-bottom: 15px;
    font-size: 1.5rem;
  }

  @media (max-width: 768px) {
    padding: 15px;
  }
`;

const TasksList = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(220px, 1fr));
  gap: 15px;

  @media (max-width: 600px) {
    grid-template-columns: repeat(1, 1fr);
  }
`;

const NoTasksMessage = styled.p`
  text-align: center;
  color: #888;
  margin-top: 20px;
`;

export default TasksPage;
