// components/UserWidget.js
import React from 'react';
import styled, { keyframes } from 'styled-components';

// Definição da animação fadeIn
const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

// Estilo para o widget
const Widget = styled.div`
  background: rgba(0, 0, 0, 0.8); /* Fundo preto semitransparente */
  border-radius: 15px;
  box-shadow: 0 8px 16px rgba(255, 255, 255, 0.2), inset 0 -3px 6px rgba(255, 255, 255, 0.1);
  padding: 2rem;
  width: 100%;
  box-sizing: border-box;
  transition: transform 0.3s ease, box-shadow 0.3s ease;
  animation: ${fadeIn} 0.6s ease-in-out;

  &:hover {
    transform: translateY(-8px);
    box-shadow: 0 12px 24px rgba(255, 255, 255, 0.3), inset 0 -4px 8px rgba(255, 255, 255, 0.2);
  }
`;

// Estilo para as informações do usuário
const UserInfo = styled.div`
  margin-top: 1.5rem;
`;

// Estilo para cada item de informação
const InfoItem = styled.div`
  margin-bottom: 0.75rem;
  font-size: 1.1rem;
  color: #dcdcdc; /* Cor clara para o texto */
`;

// Função para formatar a data
const formatDate = (dateString) => {
  if (!dateString) return 'Data não disponível.'; 
  const options = { year: 'numeric', month: 'long', day: 'numeric', hour: '2-digit', minute: '2-digit' };
  return new Date(dateString).toLocaleDateString(undefined, options);
};

// Componente principal
const UserWidget = ({ user }) => {
  return (
    <Widget>
      <h2>Informações de {user.firstName}</h2>
      <UserInfo>
        <InfoItem>
          <strong>Nome:</strong> {user.firstName}
        </InfoItem>
        <InfoItem>
          <strong>Email:</strong> {user.email}
        </InfoItem>
        <InfoItem>
          <strong>Data de Criação:</strong> {formatDate(user.createdAt)}
        </InfoItem>
      </UserInfo>
    </Widget>
  );
};

export default UserWidget;
