// TasksPage.js
import React from 'react';
import { useTasks } from '../components/Tasks/useTasks';
import TaskList from '../components/Tasks/TaskList'; // Supondo que você tenha componentes separados para a UI
import TaskModal from '../components/Tasks/TaskModal';
import TaskFilter from '../components/Tasks/TaskFilter';

const TasksPage = ({ navigate }) => {
  const {
    tasks,
    filteredTasks,
    filter,
    isAuthenticated,
    user,
    selectedTask,
    modalOpen,
    menuOpen,
    menuRef,
    newTaskTitle,
    newTaskDescription,
    setNewTaskTitle,
    setNewTaskDescription,
    handleFilterChange,
    handleCompleteTask,
    handleEditClick,
    handleCloseModal,
    handleUpdateTask,
    handleDeleteTask,
    handleAddTask,
  } = useTasks(navigate);

  if (!isAuthenticated) return <div>Loading...</div>;

  return (
    <div>
      <h1>Olá, {user.firstName}</h1>
      <TaskFilter filter={filter} onChange={handleFilterChange} />
      <TaskList
        tasks={filteredTasks}
        onComplete={handleCompleteTask}
        onEdit={handleEditClick}
        onDelete={handleDeleteTask}
      />
      {modalOpen && (
        <TaskModal
          task={selectedTask}
          newTaskTitle={newTaskTitle}
          newTaskDescription={newTaskDescription}
          setNewTaskTitle={setNewTaskTitle}
          setNewTaskDescription={setNewTaskDescription}
          onUpdate={handleUpdateTask}
          onClose={handleCloseModal}
          onAdd={handleAddTask}
        />
      )}
    </div>
  );
};

export default TasksPage;
