// TaskList.js
import React from 'react';

const styles = {
  taskList: {
    padding: '20px',
  },
  taskItem: {
    border: '1px solid #ddd',
    borderRadius: '4px',
    padding: '10px',
    marginBottom: '10px',
  },
  taskItemTitle: {
    margin: '0 0 10px',
  },
  taskItemDescription: {
    margin: '0 0 10px',
  },
  button: {
    marginRight: '5px',
  },
};

const TaskList = ({ tasks, onComplete, onEdit, onDelete }) => {
  return (
    <div style={styles.taskList}>
      {tasks.length > 0 ? (
        tasks.map(task => (
          <div key={task.id} style={styles.taskItem}>
            <h3 style={styles.taskItemTitle}>{task.title}</h3>
            <p style={styles.taskItemDescription}>{task.description}</p>
            <p>Data: {formatDate(task.createdAt)}</p>
            <button style={styles.button} onClick={() => onComplete(task.id)}>Concluir</button>
            <button style={styles.button} onClick={() => onEdit(task)}>Editar</button>
            <button style={styles.button} onClick={() => onDelete(task.id)}>Excluir</button>
          </div>
        ))
      ) : (
        <p>Nenhuma tarefa encontrada.</p>
      )}
    </div>
  );
};

// Função para formatar a data
const formatDate = (dateString) => {
  if (!dateString) return 'Data não disponível.';
  const options = { year: 'numeric', month: 'long', day: 'numeric', hour: '2-digit', minute: '2-digit' };
  return new Date(dateString).toLocaleDateString(undefined, options);
};

export default TaskList;
