import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import useUserInfo from '../hooks/Tasks/useUserInfo';
import useTasks from '../hooks/Tasks/useTasks';
import TaskCard from '../components/Tasks Home/TaskCard';
import Modal from '../components/Tasks Home/Modal';
import UserDetails from '../components/Tasks Home/userDetails';

const TasksPage = () => {
  const { username } = useParams();
  const navigate = useNavigate();
  const [selectedTask, setSelectedTask] = useState(null);

  const { firstName, fullName, email, createdAt, error, loading } = useUserInfo(username);
  const { tasks, error: tasksError } = useTasks(username);

  if (loading) {
    return <div>Carregando...</div>; // Exibir um carregamento enquanto os dados são carregados
  }

  if (error || tasksError) {
    // Redirecionar para a página de erro se houver um erro
    navigate('/error');
    return null; // Não renderizar o componente se houver erro
  }

  const handleViewTask = (task) => setSelectedTask(task);
  const handleCloseModal = () => setSelectedTask(null);

  return (
    <PageContainer>
      <Header>
        <h1>Tarefas de {firstName}</h1>
        <p>Visualize suas tarefas e detalhes do usuário</p>
      </Header>
      <Content>
        <UserDetails 
          fullName={fullName} 
          email={email} 
          createdAt={createdAt} 
          username={username} 
        />
        <TasksSection>
          <SectionTitle>Suas Tarefas</SectionTitle>
          {tasks && tasks.length > 0 ? (
            <TasksList>
              {tasks.map((task) => (
                <TaskCard
                  key={task.id}
                  task={task}
                  onViewTask={() => handleViewTask(task)}
                />
              ))}
            </TasksList>
          ) : (
            <NoTasksMessage>Não há tarefas disponíveis.</NoTasksMessage>
          )}
        </TasksSection>
      </Content>
      {selectedTask && (
        <Modal task={selectedTask} onClose={handleCloseModal} />
      )}
    </PageContainer>
  );
};

// Styled-components omitidos para brevidade

export default TasksPage;
