// Header.js
import React from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import styled from 'styled-components';

const Header = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const isAuthenticated = !!localStorage.getItem('token'); // Verifica se o usuário está autenticado

  const handleLogout = () => {
    // Remove o token de autenticação e mantém o usuário na página de tarefas
    localStorage.removeItem('token');
    navigate('/tasks');
  };

  return (
    <Container>
      <StyledLink to={isAuthenticated ? '/tasks' : '/'}>
        <Logo src="/connecter-logo-preview.png" alt="Connecter Logo" />
      </StyledLink>
      {location.pathname === '/tasks' && (
        <LogoutButton onClick={handleLogout}>Logout</LogoutButton>
      )}
    </Container>
  );
};

const Container = styled.header`
  display: flex;
  align-items: center;
  justify-content: space-between;
  background-color: #f1f1f1;
  height: 105px;
  padding: 0 20px;
  margin: 0;
`;

const StyledLink = styled(Link)`
  display: inline-block;
  text-decoration: none;
`;

const Logo = styled.img`
  width: 20.5rem;
  display: block;
  margin: 0;
  padding: 0;
`;

const LogoutButton = styled.button`
  background-color: #007aff;
  color: #fff;
  border: none;
  border-radius: 6px;
  padding: 0.7rem 1rem;
  font-size: 0.9rem;
  font-weight: bold;
  cursor: pointer;
  transition: background-color 0.3s;

  &:hover {
    background-color: #0051a8;
  }
`;

export default Header;
