import React, { useState, useEffect, useRef } from 'react';
import { FaEllipsisV } from 'react-icons/fa'; // Importando ícone de menu
import styled from 'styled-components';

const DropdownMenu = ({ onEdit, onDelete, onComplete, isCompleted }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [hoveredItem, setHoveredItem] = useState(null);
  const menuRef = useRef(null);

  const handleToggle = () => setIsOpen(!isOpen);

  const handleClickOutside = (event) => {
    if (menuRef.current && !menuRef.current.contains(event.target)) {
      setIsOpen(false);
    }
  };

  useEffect(() => {
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  return (
    <MenuContainer ref={menuRef}>
      <Icon onClick={handleToggle} />
      {isOpen && (
        <Dropdown>
          {!isCompleted && (
            <>
              <MenuItem
                onClick={() => { onComplete(); setIsOpen(false); }}
                isHovered={hoveredItem === 'complete'}
                onMouseEnter={() => setHoveredItem('complete')}
                onMouseLeave={() => setHoveredItem(null)}
              >
                Concluir
              </MenuItem>
              <MenuItem
                onClick={() => { onEdit(); setIsOpen(false); }}
                isHovered={hoveredItem === 'edit'}
                onMouseEnter={() => setHoveredItem('edit')}
                onMouseLeave={() => setHoveredItem(null)}
              >
                Editar
              </MenuItem>
            </>
          )}
          <MenuItem
            onClick={() => { onDelete(); setIsOpen(false); }}
            isHovered={hoveredItem === 'delete'}
            onMouseEnter={() => setHoveredItem('delete')}
            onMouseLeave={() => setHoveredItem(null)}
          >
            Excluir
          </MenuItem>
        </Dropdown>
      )}
    </MenuContainer>
  );
};

const MenuContainer = styled.div`
  position: relative;
`;

const Icon = styled(FaEllipsisV)`
  cursor: pointer;
  color: #4bc5f5; /* Cor do ícone de menu */
  font-size: 20px;
`;

const Dropdown = styled.div`
  position: absolute;
  right: 0;
  top: 100%;
  background-color: #2a2a2a;
  border-radius: 4px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.2);
  padding: 10px;
  z-index: 1000;
`;

const MenuItem = styled.div`
  padding: 10px;
  color: #f5f5f5;
  cursor: pointer;
  border-radius: 4px;
  background-color: ${({ isHovered }) => (isHovered ? '#4bc5f5' : 'transparent')};
  &:hover {
    background-color: #4bc5f5;
  }
`;

export default DropdownMenu;
