import React from 'react';
import { useAuth } from '../Context/AuthContext';
import Navbar from '../components/Manager/Navbar';
import TaskModal from '../components/Manager/TaskModal';
import TaskList from '../components/Manager/TaskList';
import LoadingMessage from '../components/Manager/LoadingMessage';
import NoTasksMessage from '../components/Manager/NoTasksMessage';
import useTasksApi from '../hooks/Manager Hooks/useTaskApi';
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  height: 100vh;
  background-color: #000;
  color: #fff;
  overflow: hidden;
`;

const TaskWrapper = styled.div`
  flex: 1;
  overflow-y: auto;
  padding: 20px;
`;

const TaskManager = () => {
  const { 
    tasks, 
    loading, 
    modalOpen, 
    currentTask, 
    filter, 
    setFilter, 
    setModalOpen, 
    handleAddTaskClick, 
    handleSaveTask, 
    handleCompleteTask, 
    handleDeleteTask 
  } = useTasksApi();

  return (
    <Container>
      <Navbar onAddTaskClick={handleAddTaskClick} onFilterChange={setFilter} />
      <TaskWrapper>
        {loading ? (
          <LoadingMessage />
        ) : tasks.length > 0 ? (
          <TaskList 
            tasks={tasks} 
            onEdit={(task) => {
              setCurrentTask(task);
              setModalOpen(true);
            }}
            onDelete={handleDeleteTask}
            onComplete={handleCompleteTask}
          />
        ) : (
          <NoTasksMessage />
        )}
      </TaskWrapper>
      <TaskModal 
        isOpen={modalOpen} 
        onClose={() => setModalOpen(false)} 
        onSave={handleSaveTask} 
        task={currentTask} 
      />
    </Container>
  );
};

export default TaskManager;
