import React, { useEffect, useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import styled from 'styled-components';
import { FaBars } from 'react-icons/fa';
import { useAuth } from '../../hooks/useAuth';
import UserMenu from '../Header/UserMenu';

const Header = () => {
  const { logout, userInfo, checkAuthStatus } = useAuth(); // Certifique-se de usar checkAuthStatus corretamente
  const location = useLocation();
  const isOnLoginPage = location.pathname === '/login';
  const [isVerificAuth, setIsVerificAuth] = useState(false);

  const handleLogout = () => {
    logout();
  };

  // Verifica o status de autenticação ao montar o componente
  useEffect(() => {
    const verifyAuth = async () => {
      const authStatus = await checkAuthStatus();
      setIsVerificAuth(authStatus);
    };

    verifyAuth();
  }, [checkAuthStatus]);

  return (
    <Container>
      <StyledLink to={isVerificAuth ? `/tasks/${encodeURIComponent(userInfo?.username)}` : '/'}>
        <Logo 
          src="https://firebasestorage.googleapis.com/v0/b/connectrip-10205.appspot.com/o/task%2Fconnecter-logo-preview.png?alt=media&token=5891cb1a-9b80-4908-ba62-ebf81bbeb46a" 
          alt="Connecter Logo" 
        />
      </StyledLink>

      {isVerificAuth ? (
        <MenuContainer>
          <MenuIcon>
            <FaBars />
          </MenuIcon>
          <UserMenu
            name={userInfo?.fullName}
            onLogout={handleLogout}
          />
        </MenuContainer>
      ) : (
        !isOnLoginPage && (
          <LoginButton to="/login">Fazer Login</LoginButton>
        )
      )}
    </Container>
  );
};

const Container = styled.header`
  display: flex;
  align-items: center;
  justify-content: space-between;
  background: rgba(0, 0, 0, 0.6);
  height: 80px;
  padding: 0 20px;
  margin: 0;
  box-shadow: 0 2px 6px rgba(0, 0, 0, 0.3);
  width: 100%;
  top: 0;
  left: 0;
  z-index: 1000;
  position: fixed;
  backdrop-filter: blur(8px);
`;

const StyledLink = styled(Link)`
  display: flex;
  align-items: center;
  text-decoration: none;
  height: 100%;
`;

const Logo = styled.img`
  height: 30px;
  width: auto;
  cursor: pointer;
`;

const MenuContainer = styled.div`
  display: flex;
  align-items: center;
`;

const MenuIcon = styled.div`
  margin-right: 10px;
  font-size: 1.5rem;
  cursor: pointer;
`;

const LoginButton = styled(Link)`
  padding: 10px 20px;
  background: #007aff;
  color: white;
  border-radius: 5px;
  text-decoration: none;
  font-size: 1rem;
  transition: background 0.3s;

  &:hover {
    background: #0051a8;
  }
`;

export default Header;
