// src/pages/UserTasksPage.js
import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';

const UserTasksPage = () => {
  const [tasks, setTasks] = useState([]);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState({ firstName: '', email: '' });
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');

    if (token) {
      setIsAuthenticated(true);

      try {
        const decodedToken = jwtDecode(token);
        const fullName = decodedToken.name || 'Nome não disponível';
        const firstName = capitalizeFirstLetter(fullName.split(' ')[0]);

        setUser({
          firstName,
          email: decodedToken.email || 'Email não disponível',
        });

        const fetchTasks = async () => {
          try {
            const response = await fetch('http://localhost:5000/api/tasks/', {
              headers: { Authorization: `Bearer ${token}` },
            });

            if (response.ok) {
              const data = await response.json();
              setTasks(data);
            } else {
              console.error('Erro ao carregar tarefas:', response.statusText);
            }
          } catch (error) {
            console.error('Erro ao carregar tarefas:', error);
          }
        };

        fetchTasks();
      } catch (error) {
        console.error('Erro ao decodificar o token:', error);
      }
    } else {
      navigate('/login');
    }
  }, [navigate]);

  const capitalizeFirstLetter = (string) => {
    return string.charAt(0).toUpperCase() + string.slice(1);
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'Data não disponível.';
    const options = { year: 'numeric', month: 'long', day: 'numeric', hour: '2-digit', minute: '2-digit' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  return (
    <Container>
      <Header>Tarefas de {user.firstName}</Header>
      <TaskGrid>
        {tasks.map((task) => (
          <TaskCard key={task.id}>
            <CardHeader>
              <TaskName>{task.taskTitle}</TaskName>
              <TaskDate>{formatDate(task.createdAt)}</TaskDate>
            </CardHeader>
            <TaskDescription>{task.taskDescription}</TaskDescription>
          </TaskCard>
        ))}
      </TaskGrid>
    </Container>
  );
};

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  background: #f5f5f7; /* Fundo claro e limpo */
  padding: 3rem;
  min-height: 100vh;
  color: #333;
  font-family: 'San Francisco', -apple-system, BlinkMacSystemFont, Segoe UI, Roboto, Oxygen, Ubuntu, Cantarell, Fira Sans, Droid Sans, Helvetica Neue, sans-serif;
`;

const Header = styled.h1`
  color: #1d72b8; /* Azul suave */
  font-size: 2.5rem;
  font-weight: 600;
  margin-bottom: 2rem;
  text-align: center;
  letter-spacing: 1px;
`;

const TaskGrid = styled.div`
  display: grid;
  gap: 1.5rem;
  grid-template-columns: repeat(auto-fill, minmax(280px, 1fr));
  width: 100%;
  max-width: 1200px;
`;

const TaskCard = styled.div`
  background: #ffffff; /* Branco para uma aparência limpa */
  border-radius: 12px;
  padding: 1.5rem;
  color: #333;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  transition: box-shadow 0.3s ease, transform 0.3s ease;
  display: flex;
  flex-direction: column;
  justify-content: space-between;

  &:hover {
    box-shadow: 0 8px 16px rgba(0, 0, 0, 0.2);
    transform: translateY(-5px);
  }
`;

const CardHeader = styled.div`
  margin-bottom: 1rem;
`;

const TaskName = styled.h3`
  font-size: 1.25rem;
  font-weight: 500;
  color: #1d72b8; /* Azul suave */
  margin-bottom: 0.5rem;
`;

const TaskDate = styled.p`
  font-size: 0.9rem;
  color: #999;
`;

const TaskDescription = styled.p`
  font-size: 1rem;
  line-height: 1.5;
`;

export default UserTasksPage;
