import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import { useAuth } from '../Context/AuthContext';
import { useFormState } from '../hooks/useFormState'; // Importe o novo hook

const RegisterForm = () => {
  const { register, error, clearError } = useAuth();
  const { formValues, formError, handleInputChange, setError, clearError: clearFormError } = useFormState({
    username: '',
    fullName: '',
    email: '',
    password: '',
    confirmPassword: '',
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (formValues.password !== formValues.confirmPassword) {
      setError('As senhas não coincidem');
      return;
    }
    try {
      clearFormError(); // Limpa o erro de formulário
      await register(formValues.username, formValues.fullName, formValues.email, formValues.password);
    } catch (err) {
      setError(err.message); // Atualiza com a mensagem de erro do registro
    }
  };

  const handleInputChangeWrapper = (e) => {
    handleInputChange(e);
    if (error || formError) {
      clearError(); // Limpa o erro quando qualquer campo é alterado
      clearFormError(); // Limpa o erro de formulário
    }
  };

  return (
    <Container>
      <Form onSubmit={handleSubmit}>
        <Logo
          src="https://firebasestorage.googleapis.com/v0/b/connectrip-10205.appspot.com/o/task%2FConnecter-preview.png?alt=media&token=981e1443-05f7-4bfe-bc30-cc5be9bb4c57"
          alt="Connecter Intro"
        />
        <TitleContainer>
          Criar seu ID Connecter
        </TitleContainer>
        <ScrollContainer>
          <Title>
            Um ID Connecter é o que você precisa para acessar o gerenciador.
            <br />
            <Text>
              Já tem um ID? <StyledLink to="/login">Faça login</StyledLink>
            </Text>
          </Title>
          <Input
            name="username"
            type="text"
            placeholder="Nome de Usuário"
            value={formValues.username}
            onChange={handleInputChangeWrapper}
          />
          <Input
            name="fullName"
            type="text"
            placeholder="Nome Completo"
            value={formValues.fullName}
            onChange={handleInputChangeWrapper}
          />
          <Input
            name="email"
            type="email"
            placeholder="E-mail"
            value={formValues.email}
            onChange={handleInputChangeWrapper}
          />
          <Input
            name="password"
            type="password"
            placeholder="Senha"
            value={formValues.password}
            onChange={handleInputChangeWrapper}
          />
          <Input
            name="confirmPassword"
            type="password"
            placeholder="Confirme a Senha"
            value={formValues.confirmPassword}
            onChange={handleInputChangeWrapper}
          />
        </ScrollContainer>
        <ButtonContainer>
          <Button type="submit">Registrar</Button>
        </ButtonContainer>
        {(error || formError) && <ErrorMessage aria-live="assertive">{formError || error}</ErrorMessage>}
      </Form>
    </Container>
  );
};

// Estilos CSS com styled-components
const Container = styled.div`
  display: flex;
  margin: 3rem;
  justify-content: center;
  align-items: center;
  height: 100vh;
  color: #fff;
`;

const Form = styled.form`
  padding: 2rem;
  max-width: 560px;
  width: 100%;
  max-height: 100vh;
  text-align: center;
  background: #1c1c1c;
  border-radius: 30px;
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.4);
  display: flex;
  flex-direction: column;
  align-items: center;
  position: relative;
`;

const Logo = styled.img`
  width: 200px;
  height: 190px;
  margin-bottom: -1rem;
`;

const TitleContainer = styled.h2`
  width: 100%;
  font-size: 2rem;
  font-weight: 500;
  color: #fff;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif;
  border-bottom: 2px solid #fff;
  padding-bottom: 1rem;
  margin-bottom: 1rem;
`;

const Title = styled.h2`
  width: 100%;
  margin-bottom: 1.5rem;
  font-size: 1rem;
  font-weight: 500;
  color: #fff;
`;

const Text = styled.span`
  color: #fff;
  margin-left: 5px;
`;

const StyledLink = styled(Link)`
  color: #ea4f97;
  text-decoration: none;
  font-weight: bold;

  &:hover {
    text-decoration: underline;
  }
`;

const ScrollContainer = styled.div`
  width: 100%;
  max-height: 30vh;
  overflow-y: auto;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const Input = styled.input`
  width: 70%;
  padding: 1rem;
  padding-right: 3.5rem;
  border: 1px solid #a4a4ad;
  margin-bottom: 1rem;
  border-radius: 6px;
  font-size: 1rem;
  color: #fff;
  background-color: #333;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif;
  transition: border-color 0.3s, box-shadow 0.3s;

  &:focus {
    border-color: #ea4f97;
    box-shadow: 0 0 0 2px rgba(234, 79, 151, 0.2);
    outline: none;
  }
`;

const ButtonContainer = styled.div`
  display: flex;
  width: 100%;
  justify-content: end;
  margin-top: 2rem;
  position: relative;
`;

const Button = styled.button`
  width: 6.2rem;
  height: 2.4rem;
  padding: 0.5rem 1rem;
  background-color: #ea4f97;
  border: none;
  color: #fff;
  border-radius: 6px;
  font-size: 0.9rem;
  font-weight: bold;
  cursor: pointer;
  transition: background-color 0.3s;

  &:hover {
    background-color: #d43f82;
  }

  &:disabled {
    cursor: not-allowed;
    opacity: 0.5;
  }
`;

const ErrorMessage = styled.div`
  color: red;
  margin-top: 1rem;
  position: absolute;
  font-weight: bold;
  bottom: 1rem;
  left: 50%;
  transform: translateX(-50%);
`;

export default RegisterForm;
