import React, { useState, useEffect } from 'react';

const TaskModal = ({ isOpen, onClose, onSave, task }) => {
  const [formState, setFormState] = useState({ title: '', description: '' });

  useEffect(() => {
    if (task) {
      setFormState({ title: task.taskTitle, description: task.taskDescription });
    } else {
      setFormState({ title: '', description: '' });
    }
  }, [task, isOpen]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormState((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = () => {
    onSave(formState);
    handleClose();
  };

  const handleClose = () => {
    setFormState({ title: '', description: '' }); // Limpar formulário ao fechar
    onClose();
  };

  if (!isOpen) return null;

  return (
    <div style={styles.modalOverlay}>
      <div style={styles.modalContent}>
        <h2 style={styles.title}>{task ? 'Editar Tarefa' : 'Adicionar Tarefa'}</h2>
        <input
          type="text"
          name="title"
          placeholder="Título"
          value={formState.title}
          onChange={handleChange}
          style={styles.input}
        />
        <textarea
          name="description"
          placeholder="Descrição"
          value={formState.description}
          onChange={handleChange}
          style={styles.textarea}
        />
        <div style={styles.buttonContainer}>
          <button
            onClick={handleSubmit}
            style={{ ...styles.button, ...styles.saveButton }}
          >
            Salvar
          </button>
          <button
            onClick={handleClose}
            style={{ ...styles.button, ...styles.closeButton }}
          >
            Cancelar
          </button>
        </div>
      </div>
    </div>
  );
};

const styles = {
  modalOverlay: {
    position: 'fixed',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    backgroundColor: 'rgba(0, 0, 0, 0.5)', // Fundo preto translúcido
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    zIndex: 1000,
  },
  modalContent: {
    backgroundColor: '#1e1e1e', // Fundo escuro
    padding: '20px',
    borderRadius: '12px',
    width: '400px',
    boxShadow: '0 8px 20px rgba(0, 0, 0, 0.5)',
    color: '#f5f5f5', // Texto claro
    textAlign: 'center',
    border: '1px solid #333',
    transition: 'transform 0.3s ease-in-out', // Animação de entrada
  },
  title: {
    marginBottom: '20px',
    fontSize: '1.6rem',
    borderBottom: '2px solid #ea4f97', // Linha inferior rosa
    paddingBottom: '10px',
    color: '#ea4f97',
  },
  input: {
    width: '100%',
    padding: '12px',
    marginBottom: '16px',
    border: '1px solid #444',
    borderRadius: '8px',
    backgroundColor: '#2c2c2c',
    color: '#f5f5f5',
    outline: 'none',
    fontSize: '1rem',
    transition: 'border-color 0.3s, background-color 0.3s',
  },
  textarea: {
    width: '100%',
    padding: '12px',
    marginBottom: '16px',
    border: '1px solid #444',
    borderRadius: '8px',
    backgroundColor: '#2c2c2c',
    color: '#f5f5f5',
    outline: 'none',
    fontSize: '1rem',
    height: '120px',
    transition: 'border-color 0.3s, background-color 0.3s',
    resize: 'none',
  },
  buttonContainer: {
    display: 'flex',
    justifyContent: 'space-between',
  },
  button: {
    backgroundColor: '#ea4f97', // Cor de fundo rosa
    color: '#f5f5f5', // Texto claro
    border: 'none',
    padding: '12px 16px',
    borderRadius: '8px',
    cursor: 'pointer',
    transition: 'background-color 0.3s, transform 0.2s',
    flex: 1,
    fontSize: '1rem',
    margin: '0 5px',
  },
  saveButton: {
    backgroundColor: '#41ee4a', // Verde para salvar
    ':hover': {
      backgroundColor: '#32b243', // Verde escuro ao passar o mouse
    },
  },
  closeButton: {
    backgroundColor: '#f70073', // Rosa para cancelar
    ':hover': {
      backgroundColor: '#e60066', // Rosa escuro ao passar o mouse
    },
  },
};

export default TaskModal;
