import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import styled from 'styled-components';
import { jwtDecode } from 'jwt-decode'; 

const capitalizeFirstLetter = (string) => {
  return string.charAt(0).toUpperCase() + string.slice(1);
};

const TasksPage = () => {
  const { username } = useParams(); 
  const [tasks, setTasks] = useState([]);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState({ firstName: '', email: '' });
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');

    if (token) {
      setIsAuthenticated(true);

      try {
        const decodedToken = jwtDecode(token);
        const fullName = decodedToken.name || 'Nome não disponível';
        const firstName = capitalizeFirstLetter(fullName.split(' ')[0]); 

        setUser({
          firstName,
          email: decodedToken.email || 'Email não disponível',
        });

        if (username !== firstName) {
          navigate(`/tasks/${encodeURIComponent(firstName)}`);
        }
      } catch (error) {
        console.error('Erro ao decodificar o token:', error);
      }
    } else {
      const fetchUserInfo = async () => {
        try {
          const response = await fetch(
            `http://localhost:5000/api/users/user/${encodeURIComponent(username)}`
          );
          if (response.ok) {
            const data = await response.json();
            setUser({
              firstName: capitalizeFirstLetter(data.name),
              email: data.email,
            });
          } else {
            console.error(
              'Erro ao carregar informações do usuário:',
              response.statusText
            );
          }
        } catch (error) {
          console.error('Erro ao carregar informações do usuário:', error);
        }
      };

      fetchUserInfo();
    }

    const fetchTasks = async () => {
      try {
        const response = await fetch(
          `http://localhost:5000/api/tasks/${encodeURIComponent(username)}`,
          {
            headers: {
              Authorization: token ? `Bearer ${token}` : '',
            },
          }
        );

        if (response.ok) {
          const data = await response.json();
          setTasks(data);
        } else {
          console.error('Erro ao carregar tarefas:', response.statusText);
        }
      } catch (error) {
        console.error('Erro ao carregar tarefas:', error);
      }
    };

    fetchTasks();
  }, [navigate, username]);

  const handleAction = () => {
    if (!isAuthenticated) {
      alert('Você precisa estar logado para realizar essa ação.');
    }
  };

  return (
    <Container>
      <WidgetsContainer>
        <UserWidget>
          <h2>Informações de {user.firstName}</h2>
          <UserInfo>
            <InfoItem>
              <strong>Nome:</strong> {user.firstName}
            </InfoItem>
            <InfoItem>
              <strong>Email:</strong> {user.email}
            </InfoItem>
          </UserInfo>
        </UserWidget>
        <TasksWidget>
          <h2>Tarefas de {user.firstName}</h2>
          <TaskGrid>
            {tasks.slice(0, 6).map((task) => (
              <TaskItem key={task.id}>
                <TaskName>{task.name}</TaskName>
                <ActionButton
                  onClick={handleAction}
                  disabled={!isAuthenticated}
                >
                  Editar
                </ActionButton>
              </TaskItem>
            ))}
          </TaskGrid>
        </TasksWidget>
      </WidgetsContainer>
    </Container>
  );
};

const Container = styled.div`
  display: flex;
  justify-content: center;
  background: url('/connecter-background.jpg') no-repeat center center fixed;
  background-size: cover;
  padding: 2rem;
  min-height: 100vh;
  align-items: flex-start;
  color: #e0e0e0; /* Cor de texto padrão */
`;

const WidgetsContainer = styled.div`
  display: flex;
  gap: 2rem;
  width: 100%;
  margin-top: 5rem;
  max-width: 1200px;
  justify-content: center;
`;

const Widget = styled.div`
  background: rgba(0, 0, 0, 0.8); /* Fundo preto semitransparente */
  border-radius: 15px;
  box-shadow: 0 8px 16px rgba(0, 0, 0, 0.5), inset 0 -3px 6px rgba(0, 0, 0, 0.1);
  padding: 2rem;
  width: 100%;
  box-sizing: border-box;
  transition: transform 0.3s ease, box-shadow 0.3s ease;

  &:hover {
    transform: translateY(-8px);
    box-shadow: 0 12px 24px rgba(0, 0, 0, 0.6), inset 0 -4px 8px rgba(0, 0, 0, 0.2);
  }
`;

const UserWidget = styled(Widget)`
  flex: 1;
  max-width: 350px;
  background: rgba(0, 0, 0, 0.7); /* Fundo preto mais escuro para o widget do usuário */
`;

const TasksWidget = styled(Widget)`
  flex: 2;
  max-width: 750px;
  background: rgba(0, 0, 0, 0.7); /* Fundo preto mais escuro para o widget de tarefas */
`;

const UserInfo = styled.div`
  margin-top: 1.5rem;
`;

const InfoItem = styled.div`
  margin-bottom: 0.75rem;
  font-size: 1.1rem;
  color: #e0e0e0; /* Cor do texto */
  font-weight: 500;
`;

const TaskGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(220px, 1fr));
  gap: 1.5rem;
`;

const TaskItem = styled.div`
  background: linear-gradient(145deg, #333, #444); /* Gradiente escuro para as tarefas */
  border: 1px solid #555;
  border-radius: 12px;
  padding: 1.5rem;
  display: flex;
  justify-content: space-between;
  align-items: center;
  font-size: 1rem;
  transition: background-color 0.3s ease, transform 0.3s ease;

  &:hover {
    background-color: #555;
    transform: translateY(-3px);
  }
`;

const TaskName = styled.span`
  font-size: 1.1rem;
  font-weight: 500;
  color: #e0e0e0;
`;

const ActionButton = styled.button`
  background: linear-gradient(145deg, #ff4f77, #ff1a66); /* Gradiente rosa */
  color: white;
  border: none;
  border-radius: 8px;
  padding: 0.75rem 1.25rem;
  font-size: 0.95rem;
  cursor: pointer;
  transition: background 0.3s ease, transform 0.2s ease;

  &:hover {
    background: linear-gradient(145deg, #ff1a66, #ff4f77); /* Gradiente rosa invertido */
    transform: scale(1.1);
  }

  &:disabled {
    background: #ddd;
    color: #888;
    cursor: not-allowed;
  }
`;

export default TasksPage;
