// Intro.js
import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';

const Intro = () => {
  return (
    <IntroContainer>
      <ContentWrapper>
        <Title>Organize suas tarefas com facilidade</Title>
        <Subtitle>Aumente sua produtividade e alcance seus objetivos.</Subtitle>
        <CardContainer>
          <Card>
            <PhoneImage src="https://example.com/iphone1.png" alt="App screenshot 1" />
            <CardText>Planeje seu dia com listas personalizadas</CardText>
          </Card>
          <Card>
            <PhoneImage src="https://example.com/iphone2.png" alt="App screenshot 2" />
            <CardText>Receba lembretes para nunca esquecer uma tarefa</CardText>
          </Card>
          <Card>
            <PhoneImage src="https://example.com/iphone3.png" alt="App screenshot 3" />
            <CardText>Colabore com sua equipe em tempo real</CardText>
          </Card>
        </CardContainer>
        <LoginButton to="/login">Iniciar Sess√£o</LoginButton>
      </ContentWrapper>
    </IntroContainer>
  );
};

const IntroContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  text-align: center;
  padding: 2rem;
  min-height: 100vh;
  background: #f5f5f5;
`;

const ContentWrapper = styled.div`
  max-width: 1200px;
  width: 100%;
`;

const Title = styled.h1`
  font-size: 3.5rem;
  color: #333;
  font-weight: bold;
  margin-bottom: 1rem;
  font-family: 'Open Sans', sans-serif;
`;

const Subtitle = styled.p`
  font-size: 1.5rem;
  color: #666;
  margin-bottom: 2rem;
  font-family: 'Open Sans', sans-serif;
`;

const CardContainer = styled.div`
  display: flex;
  justify-content: space-around;
  margin-bottom: 2rem;
  gap: 1rem;
`;

const Card = styled.div`
  background: #fff;
  border-radius: 10px;
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1);
  padding: 1rem;
  max-width: 300px;
  text-align: center;
`;

const PhoneImage = styled.img`
  width: 100%;
  border-radius: 10px;
`;

const CardText = styled.p`
  margin-top: 1rem;
  font-size: 1rem;
  color: #333;
  font-weight: bold;
`;

const LoginButton = styled(Link)`
  background-color: #333;
  color: #fff;
  border: none;
  padding: 0.75rem 2rem;
  border-radius: 20px;
  font-size: 1.1rem;
  font-weight: 600;
  cursor: pointer;
  transition: background-color 0.3s;
  text-decoration: none;

  &:hover {
    background-color: #555;
  }
`;

export default Intro;
