import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import styled, { keyframes } from 'styled-components';
import { useAuth } from '../hooks/useAuth'; // Importando o hook useAuth
import { jwtDecode } from 'jwt-decode';

const capitalizeFirstLetter = (string) => {
  return string.charAt(0).toUpperCase() + string.slice(1);
};

const formatDate = (dateString) => {
  const options = { year: 'numeric', month: 'long', day: 'numeric', hour: '2-digit', minute: '2-digit' };
  return new Date(dateString).toLocaleDateString(undefined, options);
};

const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const TasksPage = () => {
  const { username } = useParams(); // Obtém o nome de usuário da URL
  const [tasks, setTasks] = useState([]);
  const [selectedTask, setSelectedTask] = useState(null);
  const [showLoginModal, setShowLoginModal] = useState(false);
  const [fullName, setFullName] = useState('');
  const [email, setEmail] = useState('');
  const navigate = useNavigate();

  const { token, userInfo } = useAuth(); // Utilize useAuth para obter informações do usuário

  useEffect(() => {
    const fetchUserInfo = async () => {
      try {
        if (token) {
          const decodedToken = jwtDecode(token);
          const firstName = capitalizeFirstLetter(decodedToken.username || 'Usuário');
          setFullName(decodedToken.fullName || 'Nome não disponível');
          setEmail(decodedToken.email || 'Email não disponível');

          // Redirecionar se o nome de usuário da URL não corresponder ao nome do usuário autenticado
          if (username !== firstName) {
            navigate(`/tasks/${encodeURIComponent(firstName)}`);
            return;
          }
        } else {
          const response = await fetch(`http://localhost:5000/api/users/user/${encodeURIComponent(username)}`);
          if (response.ok) {
            const data = await response.json();
            setFullName(data.fullName || 'Nome não disponível');
            setEmail(data.email || 'Email não disponível');
          } else {
            console.error('Erro ao carregar informações do usuário:', response.statusText);
          }
        }
      } catch (error) {
        console.error('Erro ao carregar informações do usuário:', error);
      }
    };

    const fetchTasks = async () => {
      try {
        const apiUrl = token
          ? `http://localhost:5000/api/tasks/user/${encodeURIComponent(userInfo.username)}`
          : `http://localhost:5000/api/tasks/user/${encodeURIComponent(username)}`; // Correção para usar o username da URL

        const response = await fetch(apiUrl, {
          headers: token ? { Authorization: `Bearer ${token}` } : {},
        });

        if (response.ok) {
          const data = await response.json();
          setTasks(data);
        } else {
          console.error('Erro ao carregar tarefas:', response.statusText);
        }
      } catch (error) {
        console.error('Erro ao carregar tarefas:', error);
      }
    };

    fetchUserInfo();
    fetchTasks();
  }, [navigate, username, token, userInfo.username]);

  const handleViewTask = (task) => {
    if (token) {
      setSelectedTask(task);
    } else {
      setShowLoginModal(true);
    }
  };

  const handleCloseModal = () => {
    setSelectedTask(null);
  };

  const handleCloseLoginModal = () => {
    setShowLoginModal(false);
  };

  return (
    <Container>
      <WidgetsContainer>
        <UserWidget>
          <h2>Informações de {fullName}</h2> {/* Exibe o nome do usuário do token */}
          <UserInfo>
            <InfoItem>
              <strong>Usuário:</strong> {username}
            </InfoItem>
            <InfoItem>
              <strong>Email:</strong> {email}
            </InfoItem>
            <InfoItem>
              <strong>Data de Criação:</strong> {/* Exibir data de criação, se disponível */}
            </InfoItem>
          </UserInfo>
        </UserWidget>
        <TasksWidget>
          <h2>Tarefas de {fullName}</h2> {/* Exibe o nome do usuário do token */}
          <TaskGrid>
            {tasks.slice(0, 6).map((task) => (
              <TaskItem key={task.id}>
                <TaskName>{task.taskTitle}</TaskName>
                <TaskDate>{formatDate(task.createdAt)}</TaskDate>
                <ActionButton
                  onClick={() => handleViewTask(task)}
                >
                  Detalhes
                </ActionButton>
              </TaskItem>
            ))}
          </TaskGrid>
        </TasksWidget>
      </WidgetsContainer>

      {selectedTask && (
        <TaskModal>
          <ModalContent>
            <CloseButton onClick={handleCloseModal}>×</CloseButton>
            <ModalHeader>
              <h3>{selectedTask.taskTitle}</h3>
              <ModalDate>{formatDate(selectedTask.createdAt)}</ModalDate>
            </ModalHeader>
            <ModalBody>
              <p><strong>Descrição:</strong> {selectedTask.taskDescription}</p>
              <p><strong>Status:</strong> {selectedTask.taskStatus ? 'Concluído' : 'Pendente'}</p>
            </ModalBody>
          </ModalContent>
        </TaskModal>
      )}

      {showLoginModal && (
        <LoginModal>
          <LoginModalContent>
            <CloseButton onClick={handleCloseLoginModal}>×</CloseButton>
            <h2>Você precisa estar logado para ver esta tarefa</h2>
            <LoginButton onClick={() => navigate('/login')}>Login</LoginButton>
          </LoginModalContent>
        </LoginModal>
      )}
    </Container>
  );
};

const Container = styled.div`
  display: flex;
  justify-content: center;
  background: #000; /* Preto */
  padding: 2rem;
  min-height: 85vh;
  align-items: flex-start;
  color: #fff; /* Branco */
`;

const WidgetsContainer = styled.div`
  display: flex;
  gap: 2rem;
  width: 100%;
  margin-top: 5rem;
  max-width: 1200px;
  justify-content: center;
`;

const Widget = styled.div`
  background: #111; /* Preto muito escuro */
  border-radius: 10px;
  box-shadow: 0 4px 8px rgba(255, 255, 255, 0.1), inset 0 -2px 4px rgba(255, 255, 255, 0.05);
  padding: 2rem;
  width: 100%;
  box-sizing: border-box;
  transition: transform 0.3s ease, box-shadow 0.3s ease;
  animation: ${fadeIn} 0.6s ease-in-out;

  &:hover {
    transform: translateY(-4px);
    box-shadow: 0 6px 12px rgba(255, 255, 255, 0.2), inset 0 -2px 4px rgba(255, 255, 255, 0.1);
  }
`;

const TasksWidget = styled(Widget)`
  flex: 2;
  max-width: 750px;

  h2 {
    color: #fff; /* Branco */
    font-weight: bold;
    font-size: 1.6rem;
    margin-bottom: 1rem;
  }
`;

const UserWidget = styled(Widget)`
  flex: 1;
  max-width: 350px;
  height: auto;

  h2 {
    color: #fff; /* Branco */
    font-weight: bold;
    font-size: 1.6rem;
    margin-bottom: 1rem;
  }
`;

const UserInfo = styled.div`
  margin-top: 1rem;
