import { useState, useEffect } from 'react';

/**
 * Hook para gerenciar o estado do formulário.
 * Inclui funções para lidar com mudanças e resetar o formulário.
 * 
 * @param {Object} initialData - Dados iniciais para o formulário.
 */
const useFormManager = (initialData = {}) => {
  const [formData, setFormData] = useState(initialData);

  /**
   * Atualiza o estado do formulário quando os dados iniciais mudam.
   */
  useEffect(() => {
    setFormData(initialData);
  }, [initialData]);

  /**
   * Manipula mudanças nos campos do formulário.
   * 
   * @param {Event} e - Evento de mudança do campo.
   */
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  /**
   * Reseta os dados do formulário para os dados iniciais.
   */
  const resetForm = () => {
    setFormData(initialData);
  };

  return {
    formData,
    handleChange,
    resetForm,
  };
};

export default useFormManager;
