// Intro.js
import React from 'react';
import styled from 'styled-components';

const Intro = () => {
  return (
    <IntroContainer>
      <Image src="https://images.pexels.com/photos/5257456/pexels-photo-5257456.jpeg" alt="Pessoa segurando um papel branco" />
      <IntroText>
        O melhor lugar para gerenciar suas tarefas e muito mais.
      </IntroText>
      <LoginButton>Iniciar Sessão</LoginButton>
    </IntroContainer>
  );
};

const IntroContainer = styled.div`
  text-align: center;
  margin-bottom: 2rem; /* Ajusta a margem inferior para criar espaço suficiente */
`;

const Image = styled.img`
  width: 100%; /* Ajuste a largura conforme necessário */
  max-width: 300px; /* Limite a largura máxima */
  margin-bottom: 1rem; /* Espaço entre a imagem e o texto */
  border-radius: 8px; /* Adiciona bordas arredondadas se desejado */
`;

const IntroText = styled.p`
  font-size: 1.2rem;
  color: #333;
  margin: 0;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto,
    'Helvetica Neue', Arial, sans-serif;
  margin-bottom: 1rem; /* Espaço entre o texto e o botão */
`;

const LoginButton = styled.button`
  background-color: #007aff; /* Cor do botão */
  color: #fff;
  border: none;
  padding: 0.75rem 1.5rem;
  border-radius: 8px;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: background-color 0.3s;

  &:hover {
    background-color: #0051a8; /* Cor do botão ao passar o mouse */
  }

  &:focus {
    outline: 2px solid #0051a8; /* Cor do contorno ao focar no botão */
  }
`;

export default Intro;
