// Intro.js
import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';

const Intro = () => {
  return (
    <IntroContainer>
      <LogoContainer>
        <Logo src="/connecter-intro-preview.png" alt="Connecter Intro" />
      </LogoContainer>
      <Title>Connecter</Title>
      <IntroText>
        O melhor lugar para gerenciar suas tarefas e muito mais.
      </IntroText>
      <LoginButton to="/login">Iniciar Sessão</LoginButton>
    </IntroContainer>
  );
};

const IntroContainer = styled.div`
  text-align: center;
  padding: 2rem; /* Espaçamento interno para melhor visualização */
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  min-height: 100vh; /* Garante que o conteúdo fique centralizado verticalmente */
`;

const LogoContainer = styled.div`
  display: inline-block; /* Faz com que o contêiner só ocupe o espaço da logo */
  margin-bottom: 0.5rem; /* Espaço entre o logo e o título */
`;

const Logo = styled.img`
  width: 450px; /* Ajuste o tamanho conforme necessário */
  display: block; /* Remove espaços indesejados ao redor da imagem */
`;

const Title = styled.h1`
  font-size: 6rem;
  color: #000;
  font-weight: bold;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto,
    'Helvetica Neue', Arial, sans-serif;
  margin-bottom: 1rem; /* Espaço entre o título e o texto */
`;

const IntroText = styled.p`
  font-size: 2rem;
  color: #000;
  font-weight: bold;
  margin: 1rem auto;
  max-width: 500px; /* Limita a largura do texto */
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto,
    'Helvetica Neue', Arial, sans-serif;
  margin-bottom: 2rem; /* Espaço entre o texto e o botão */
`;

const LoginButton = styled(Link)`
  background-color: #000; /* Cor do botão */
  color: #fff;
  border: none;
  padding: 0.75rem 2rem;
  border-radius: 20px;
  font-size: 1.1rem;
  font-weight: 600;
  cursor: pointer;
  transition: background-color 0.3s;
  text-decoration: none;
  margin-top: 1rem; /* Espaço entre o texto e o botão */

  &:hover {
    color: #000;
    background-color: gray; /* Cor do botão ao passar o mouse */
  }
`;

export default Intro;
