import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import styled from 'styled-components';
import { useAuth } from '../Context/AuthContext';

const formatDate = (dateString) => {
  const options = { day: '2-digit', month: '2-digit', year: 'numeric' };
  return new Date(dateString).toLocaleDateString('pt-BR', options);
};

const Loader = () => (
  <LoaderOverlay>
    <LoaderSpinner />
  </LoaderOverlay>
);

const TaskLoader = () => (
  <TaskLoaderOverlay>
    <TaskLoaderSpinner />
  </TaskLoaderOverlay>
);

const TasksPage = () => {
  const { username } = useParams();
  const [tasks, setTasks] = useState([]);
  const [selectedTask, setSelectedTask] = useState(null);
  const [firstName, setFirstName] = useState('');
  const [email, setEmail] = useState('');
  const [createdAt, setCreatedAt] = useState('');
  const [loading, setLoading] = useState(true);
  const { token, userInfo } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserInfo = async () => {
      const cachedUserInfo = localStorage.getItem(`userInfo_${username}`);
      if (cachedUserInfo) {
        const { firstName, email, createdAt } = JSON.parse(cachedUserInfo);
        setFirstName(firstName);
        setEmail(email);
        setCreatedAt(createdAt);
      } else {
        try {
          const response = await fetch(`http://localhost:5000/api/users/user/${username}`);
          if (response.ok) {
            const data = await response.json();
            const nameParts = data.fullName ? data.fullName.split(' ') : [];
            const firstName = nameParts[0] || 'Nome não disponível';
            const email = data.email || 'Email não disponível';
            const createdAt = formatDate(data.createdAt) || 'Data não disponível';
            setFirstName(firstName);
            setEmail(email);
            setCreatedAt(createdAt);
            localStorage.setItem(`userInfo_${username}`, JSON.stringify({ firstName, email, createdAt }));
          } else {
            console.error('Erro ao carregar informações do usuário:', response.statusText);
          }
        } catch (error) {
          console.error('Erro ao carregar informações do usuário:', error);
        }
      }
    };

    const fetchTasks = async () => {
      const cachedTasks = localStorage.getItem(`tasks_${username}`);
      if (cachedTasks) {
        setTasks(JSON.parse(cachedTasks));
        setLoading(false);
      } else {
        try {
          const apiUrl = token
            ? `http://localhost:5000/api/manager/tasks/${encodeURIComponent(userInfo.username)}`
            : `http://localhost:5000/api/manager/tasks/${encodeURIComponent(username)}`;

          const response = await fetch(apiUrl, {
            headers: token ? { Authorization: `Bearer ${token}` } : {},
          });

          if (response.ok) {
            const data = await response.json();
            const sortedTasks = data.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
            const recentTasks = sortedTasks.slice(0, 9);
            setTasks(recentTasks);
            localStorage.setItem(`tasks_${username}`, JSON.stringify(recentTasks));
            setLoading(false);
          } else {
            console.error('Erro ao carregar tarefas:', response.statusText);
          }
        } catch (error) {
          console.error('Erro ao carregar tarefas:', error);
        }
      }
    };

    fetchUserInfo();
    fetchTasks();
  }, [navigate, username, token, userInfo.username]);

  const handleViewTask = (task) => setSelectedTask(task);

  const handleCloseModal = () => setSelectedTask(null);

  return (
    <PageContainer>
      {loading ? <Loader /> : (
        <>
          <Header>
            <h1>Dashboard de {firstName}</h1>
            <p>Visualize suas tarefas e detalhes do usuário</p>
          </Header>
          <Content>
            <UserDetails>
              <h2>Informações do Usuário</h2>
              <p><strong>Usuário:</strong> {username}</p>
              <p><strong>Email:</strong> {email}</p>
              <p><strong>Data de Criação:</strong> {createdAt}</p>
            </UserDetails>
            <TasksSection>
              <h2>Suas Tarefas</h2>
              {tasks.length > 0 ? (
                <TasksList>
                  {tasks.map((task) => (
                    <TaskCard key={task.id}>
                      <TaskCardContent>
                        <TaskTitle>Título: {task.taskTitle}</TaskTitle>
                        <TaskInfo>Criada em: {formatDate(task.createdAt)}</TaskInfo>
                        <ViewButton onClick={() => handleViewTask(task)}>Ver Tarefa</ViewButton>
                        <TaskLoader />
                      </TaskCardContent>
                    </TaskCard>
                  ))}
                </TasksList>
              ) : (
                <NoTasksMessage>Não há tarefas disponíveis.</NoTasksMessage>
              )}
            </TasksSection>
          </Content>

          {selectedTask && (
            <ModalOverlay onClick={handleCloseModal}>
              <ModalContent onClick={(e) => e.stopPropagation()}>
                <ModalHeader>
                  <h3>
                    <TitleLabel>Título:</TitleLabel>
                    <TaskTitle>{selectedTask.taskTitle}</TaskTitle>
                  </h3>
                  <CloseButton onClick={handleCloseModal}>×</CloseButton>
                </ModalHeader>
                <ModalBody>
                  <p><strong>Descrição:</strong> {selectedTask.taskDescription}</p>
                  <p><strong>Status:</strong> {selectedTask.taskStatus ? 'Concluído' : 'Pendente'}</p>
                </ModalBody>
              </ModalContent>
            </ModalOverlay>
          )}
        </>
      )}
    </PageContainer>
  );
};

