// TasksPage.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useTasks } from './hooks/useTasks';
import TaskWidget from './components/TaskWidget';
import UserWidget from './components/UserWidget';
import TaskModal from './components/TaskModal';
import styled from 'styled-components';

const TasksPage = () => {
  const { tasks, user, isAuthenticated } = useTasks();
  const [selectedTask, setSelectedTask] = useState(null);
  const [showLoginModal, setShowLoginModal] = useState(false);
  const navigate = useNavigate();

  const handleViewTask = (task) => {
    if (isAuthenticated) {
      setSelectedTask(task);
    } else {
      setShowLoginModal(true);
    }
  };

  const handleCloseModal = () => {
    setSelectedTask(null);
  };

  const handleCloseLoginModal = () => {
    setShowLoginModal(false);
  };

  return (
    <Container>
      <WidgetsContainer>
        <UserWidget user={user} />
        <TaskWidget tasks={tasks} onViewTask={handleViewTask} />
      </WidgetsContainer>

      {selectedTask && (
        <TaskModal task={selectedTask} onClose={handleCloseModal} />
      )}

      {showLoginModal && (
        <LoginModal>
          <LoginModalContent>
            <CloseButton onClick={handleCloseLoginModal}>×</CloseButton>
            <h2>Você precisa estar logado para ver esta tarefa</h2>
            <LoginButton onClick={() => navigate('/login')}>Login</LoginButton>
          </LoginModalContent>
        </LoginModal>
      )}
    </Container>
  );
};

const Container = styled.div`
  // Estilos aqui
`;

const WidgetsContainer = styled.div`
  display: flex;
  gap: 20px;
`;

const LoginModal = styled.div`
  // Estilos aqui
`;

const LoginModalContent = styled.div`
  // Estilos aqui
`;

const CloseButton = styled.button`
  // Estilos aqui
`;

const LoginButton = styled.button`
  // Estilos aqui
`;

export default TasksPage;
