import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../hooks/useAuth';

const TaskManager = () => {
  const [tasks, setTasks] = useState([]);
  const [filter, setFilter] = useState('all'); // Filtros: 'all', 'completed', 'pending'
  const [newTask, setNewTask] = useState({ title: '', description: '' });
  const [editTask, setEditTask] = useState(null);
  const { token, userInfo } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchTasks = async () => {
      if (!token || !userInfo?.username) return;
      try {
        const response = await fetch(`http://localhost:5000/api/manager/tasks/${userInfo.username}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        if (!response.ok) throw new Error('Erro ao buscar tarefas');
        const data = await response.json();
        setTasks(data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchTasks();
  }, [token, userInfo?.username]);

  const addTask = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/manager/tasks', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(newTask),
      });

      if (!response.ok) throw new Error('Erro ao adicionar tarefa');
      const newTaskData = await response.json();
      setTasks([...tasks, newTaskData.userTask]);
      setNewTask({ title: '', description: '' });
    } catch (error) {
      console.error(error);
    }
  };

  const updateTask = async (taskId, updatedTask) => {
    try {
      const response = await fetch(`http://localhost:5000/api/manager/tasks/${taskId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(updatedTask),
      });

      if (!response.ok) throw new Error('Erro ao atualizar tarefa');
      const updatedTaskData = await response.json();
      setTasks(tasks.map(task => (task.id === taskId ? updatedTaskData.userTask : task)));
      setEditTask(null); // Close the edit form
    } catch (error) {
      console.error(error);
    }
  };

  const completeTask = async (taskId) => {
    try {
      const response = await fetch(`http://localhost:5000/api/manager/tasks/${taskId}/complete`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) throw new Error('Erro ao marcar tarefa como concluída');
      const updatedTaskData = await response.json();
      setTasks(tasks.map(task => (task.id === taskId ? updatedTaskData.userTask : task)));
    } catch (error) {
      console.error(error);
    }
  };

  const deleteTask = async (taskId) => {
    try {
      const response = await fetch(`http://localhost:5000/api/manager/tasks/${taskId}`, {
        method: 'DELETE',
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) throw new Error('Erro ao excluir tarefa');
      setTasks(tasks.filter(task => task.id !== taskId));
    } catch (error) {
      console.error(error);
    }
  };

  const filteredTasks = tasks.filter(task => {
    if (filter === 'completed') return task.taskStatus;
    if (filter === 'pending') return !task.taskStatus;
    return true;
  });

  return (
    <div style={styles.container}>
      <h1 style={styles.heading}>Gerenciador de Tarefas</h1>
      <div style={styles.filterContainer}>
        <button style={styles.filterButton} onClick={() => setFilter('all')}>Todas</button>
        <button style={styles.filterButton} onClick={() => setFilter('completed')}>Concluídas</button>
        <button style={styles.filterButton} onClick={() => setFilter('pending')}>Pendentes</button>
      </div>
      <div style={styles.taskForm}>
        {editTask ? (
          <div style={styles.editForm}>
            <input
              type="text"
              placeholder="Título"
              value={editTask.title}
              onChange={(e) => setEditTask({ ...editTask, title: e.target.value })}
              style={styles.input}
            />
            <textarea
              placeholder="Descrição"
              value={editTask.description}
              onChange={(e) => setEditTask({ ...editTask, description: e.target.value })}
              style={styles.textarea}
            />
            <button onClick={() => updateTask(editTask.id, editTask)} style={styles.updateButton}>Salvar</button>
            <button onClick={() => setEditTask(null)} style={styles.cancelButton}>Cancelar</button>
          </div>
        ) : (
          <div style={styles.newTaskForm}>
            <input
              type="text"
              placeholder="Título"
              value={newTask.title}
              onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
              style={styles.input}
            />
            <textarea
              placeholder="Descrição"
              value={newTask.description}
              onChange={(e) => setNewTask({ ...newTask, description: e.target.value })}
              style={styles.textarea}
            />
            <button onClick={addTask} style={styles.addButton}>Adicionar Tarefa</button>
          </div>
        )}
      </div>
      <ul style={styles.taskList}>
        {filteredTasks.map(task => (
          <li key={task.id} style={styles.taskItem}>
            <h3 style={styles.taskTitle}>{task.taskTitle}</h3>
            <p style={styles.taskDescription}>{task.taskDescription}</p>
            <button 
              onClick={() => completeTask(task.id)} 
              style={styles.updateButton}
            >
              {task.taskStatus ? 'Marcar como Pendente' : 'Marcar como Concluída'}
            </button>
            <button onClick={() => deleteTask(task.id)} style={styles.deleteButton}>Excluir</button>
            <button onClick={() => setEditTask(task)} style={styles.editButton}>Editar</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    backgroundColor: '#000',
    color: '#fff',
    minHeight: '100vh',
    padding: '20px',
  },
  heading: {
    marginBottom: '20px',
    fontSize: '2rem',
  },
  filterContainer: {
    marginBottom: '20px',
  },
  filterButton: {
    backgroundColor: '#333',
    color: '#fff',
    border: 'none',
    padding: '10px 20px',
    margin: '0 5px',
    cursor: 'pointer',
    borderRadius: '5px',
  },
  taskForm: {
    marginBottom: '20px',
    width: '100%',
    maxWidth: '600px',
  },
  newTaskForm: {
    display: 'flex',
    flexDirection: 'column',
  },
  editForm: {
    display: 'flex',
    flexDirection: 'column',
  },
  input: {
    padding: '10px',
    marginBottom: '10px',
    border: '1px solid #666',
    backgroundColor: '#222',
    color: '#fff',
    borderRadius: '5px',
  },
  textarea: {
    padding: '10px',
    marginBottom: '10px',
    border: '1px solid #666',
    backgroundColor: '#222',
    color: '#fff',
    borderRadius: '5px',
  },
  addButton: {
    backgroundColor: '#555',
    color: '#fff',
    border: 'none',
    padding: '10px',
    cursor: 'pointer',
    borderRadius: '5px',
  },
  updateButton: {
    backgroundColor: '#777',
    color: '#fff',
    border: 'none',
    padding: '5px 10px',
    marginRight: '5px',
    cursor: 'pointer',
    borderRadius: '5px',
  },
  cancelButton: {
    backgroundColor: '#444',
    color: '#fff',
    border: 'none',
    padding: '5px 10px',
    cursor: 'pointer',
    borderRadius: '5px',
  },
  deleteButton: {
    backgroundColor: '#f00',
    color: '#fff',
    border: 'none',
    padding: '5px 10px',
    cursor: 'pointer',
    borderRadius: '5px',
  },
  editButton: {
    backgroundColor: '#666',
    color: '#fff',
    border: 'none',
    padding: '5px 10px',
    cursor: 'pointer',
    borderRadius: '5px',
  },
  taskList: {
    listStyle: 'none',
    padding: 0,
    width: '100%',
    maxWidth: '600px',
  },
  taskItem: {
    backgroundColor: '#333',
    padding: '15px',
    marginBottom: '10px',
    borderRadius: '5px',
  },
  taskTitle: {
    fontSize: '1.2rem',
    margin: '0 0 10px 0',
  },
  taskDescription: {
    margin: '0 0 10px 0',
  },
};

export default TaskManager;
