import React from 'react';
import styled from 'styled-components';

const Button = (props) => <StyledButton {...props} />;

const StyledButton = styled.button`
  position: absolute;
  top: 50%;
  right: 3.6rem;
  transform: translateY(-50%);
  width: 2.2rem;
  height: 2.2rem;
  background: ${(props) =>
    props.emptyEmail ? '#a4a4ad' : '#000'};
  border: none;
  color: #fff;
  border-radius: 50%;
  font-size: 1rem;
  font-weight: bold;
  cursor: ${(props) =>
    props.emptyEmail ? 'default' : 'pointer'};
  padding: 0;
  display: flex;
  align-items: center;
  justify-content: center;

  &:hover {
    background: ${(props) =>
      props.emptyEmail ? '#a4a4ad' : '#141414'};
  }

  &:disabled {
    cursor: not-allowed;
    opacity: 0.5;
  }
`;

export default Button;
