// Header.js
import React, { useState } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { FaBars, FaUserCircle } from 'react-icons/fa';

const Header = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isUserMenuOpen, setIsUserMenuOpen] = useState(false);
  const isAuthenticated = !!localStorage.getItem('token'); // Verifica se o usuário está autenticado
  const username = 'Usuário'; // Substitua com o nome de usuário real

  const handleLogout = () => {
    // Remove o token de autenticação e mantém o usuário na página de tarefas
    localStorage.removeItem('token');
    navigate('/tasks');
  };

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const toggleUserMenu = () => {
    setIsUserMenuOpen(!isUserMenuOpen);
  };

  return (
    <Container>
      <StyledLink to={isAuthenticated ? '/tasks' : '/'}>
        <Logo src="/connecter-logo-preview.png" alt="Connecter Logo" />
      </StyledLink>
      {isAuthenticated && (
        <MenuContainer>
          <MenuIcon onClick={toggleMenu}>
            <FaBars />
          </MenuIcon>
          <UserMenu>
            <UserIcon onClick={toggleUserMenu}>
              <FaUserCircle />
            </UserIcon>
            {isUserMenuOpen && (
              <DropdownMenu>
                <UserName>{username}</UserName>
                <LogoutButton onClick={handleLogout}>Logout</LogoutButton>
              </DropdownMenu>
            )}
          </UserMenu>
          {isMenuOpen && (
            <TaskMenu>
              <TaskLink to="/create-task">Criar Tarefa</TaskLink>
              <TaskLink to="/tasks">Ver Tarefas</TaskLink>
            </TaskMenu>
          )}
        </MenuContainer>
      )}
    </Container>
  );
};

const Container = styled.header`
  display: flex;
  align-items: center;
  justify-content: space-between;
  background-color: #f1f1f1;
  height: 105px;
  padding: 0 20px;
  margin: 0;
`;

const StyledLink = styled(Link)`
  display: inline-block;
  text-decoration: none;
`;

const Logo = styled.img`
  width: 20.5rem;
  display: block;
  margin: 0;
  padding: 0;
`;

const MenuContainer = styled.div`
  display: flex;
  align-items: center;
`;

const MenuIcon = styled.div`
  font-size: 1.5rem;
  cursor: pointer;
  margin-right: 1rem;
`;

const UserMenu = styled.div`
  position: relative;
`;

const UserIcon = styled.div`
  font-size: 1.5rem;
  cursor: pointer;
`;

const DropdownMenu = styled.div`
  position: absolute;
  top: 100%;
  right: 0;
  background-color: #fff;
  border: 1px solid #ddd;
  border-radius: 6px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
  padding: 0.5rem;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const UserName = styled.div`
  font-size: 1rem;
  font-weight: bold;
  margin-bottom: 0.5rem;
`;

const LogoutButton = styled.button`
  background-color: #007aff;
  color: #fff;
  border: none;
  border-radius: 6px;
  padding: 0.5rem 1rem;
  font-size: 0.9rem;
  font-weight: bold;
  cursor: pointer;
  transition: background-color 0.3s;

  &:hover {
    background-color: #0051a8;
  }
`;

const TaskMenu = styled.div`
  position: absolute;
  top: 100%;
  right: 0;
  background-color: #fff;
  border: 1px solid #ddd;
  border-radius: 6px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
  padding: 0.5rem;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const TaskLink = styled(Link)`
  font-size: 1rem;
  color: #007aff;
  text-decoration: none;
  padding: 0.5rem;
  transition: background-color 0.3s;

  &:hover {
    background-color: #f1f1f1;
  }
`;

export default Header;
