import React, { useState } from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import { FaArrowRight } from 'react-icons/fa';
import { useAuth } from '../Context/AuthContext';
import { useFormState } from '../hooks/useFormState'; // Importe o novo hook

const LoginPage = () => {
  const { login, error } = useAuth();
  const { formValues, formError, handleInputChange, setError, clearError } = useFormState({
    email: '',
    password: '',
  });
  const [showPassword, setShowPassword] = useState(false);
  const [loadingStartTime, setLoadingStartTime] = useState(null); // Hora de início do carregamento
  const [minLoadingDuration] = useState(5000); // 5 segundos

  const validateEmail = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const handleShowPassword = () => {
    if (validateEmail(formValues.email)) {
      setShowPassword(true);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoadingStartTime(Date.now()); // Registra o tempo de início do carregamento

    try {
      await login(formValues.email, formValues.password);

      // Calcula o tempo decorrido e o tempo restante
      const elapsedTime = Date.now() - loadingStartTime;
      const remainingTime = Math.max(minLoadingDuration - elapsedTime, 0);
    } catch (err) {
      console.error('Erro ao fazer login:', err);
    }
  };

  return (
    <Container>
      <Form onSubmit={handleSubmit}>
        <Logo
          src="https://firebasestorage.googleapis.com/v0/b/connectrip-10205.appspot.com/o/task%2FConnecter-preview.png?alt=media&token=981e1443-05f7-4bfe-bc30-cc5be9bb4c57"
          alt="Connecter Intro"
        />
        <Title>Faça login com seu ID Connecter</Title>
        <EmailContainer>
          <Input
            name="email"
            type="email"
            placeholder="E-mail"
            value={formValues.email}
            onChange={handleInputChange}
            autoFocus
          />
          {!showPassword && (
            <Button
              onClick={handleShowPassword}
              disabled={!validateEmail(formValues.email)}
              emptyEmail={!formValues.email}
            >
              <FaArrowRight />
            </Button>
          )}
        </EmailContainer>
        <PasswordContainer show={showPassword}>
          <Input
            name="password"
            type="password"
            placeholder="Senha"
            value={formValues.password}
            onChange={handleInputChange}
          />
          <Button type="submit">
            <FaArrowRight />
          </Button>
        </PasswordContainer>
        <RegisterLink to="/register">Criar um ID Connecter</RegisterLink>
        {error && <ErrorMessage>{error}</ErrorMessage>}
      </Form>
    </Container>
  );
};

// Estilos CSS com styled-components


const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 60vh;
  margin-top: 8rem; /* Ajuste para trazer a imagem mais para cima */
`;

const Form = styled.form`
  padding: 2rem;
  max-width: 560px;
  width: 100%;
  text-align: center;
  background: #1c1c1c; /* Fundo do formulário em cinza escuro */
  border-radius: 30px;
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.4);
`;

const Logo = styled.img`
  width: 200px; /* Ajuste o tamanho conforme necessário */
  height: 190px;
  margin-bottom: -1rem; /* Diminuído o espaço entre o logo e o título */
`;

const Title = styled.h2`
  margin-bottom: 2rem;
  font-size: 1.5rem;
  font-weight: 500;
  color: #fff; /* Texto branco */
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto,
    'Helvetica Neue', Arial, sans-serif;
`;

const EmailContainer = styled.div`
  position: relative;
  width: 100%;
  margin-bottom: 1rem;
`;

const PasswordContainer = styled.div`
  position: relative;
  width: 100%;
  opacity: ${(props) => (props.show ? 1 : 0)};
  transform: ${(props) => (props.show ? 'translateY(0)' : 'translateY(-20px)')};
  transition: opacity 0.5s ease, transform 0.5s ease; /* Adiciona a animação */
`;


const Input = styled.input`
  width: 70%;
  padding: 1.2rem;
  padding-right: 3.5rem; /* Aumenta o padding para criar mais espaço para o botão */
  border: 1px solid #a4a4ad;
  border-radius: 12px;
  font-size: 1rem;
  color: #fff; /* Texto branco */
  background-color: #333; /* Fundo de input escuro */
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto,
    'Helvetica Neue', Arial, sans-serif;
  transition:
    border-color 0.3s,
    box-shadow 0.3s;

  &:focus {
    border-color: #ea4f97; /* Rosa de foco */
    box-shadow: 0 0 0 2px rgba(234, 79, 151, 0.2); /* Sombra de foco rosa */
    outline: none;
  }
`;

const Button = styled.button`
  position: absolute;
  top: 50%;
  right: 3.6rem; /* Ajusta a posição do botão */
  transform: translateY(-50%);
  width: 2.2rem; /* Ajustar para o tamanho desejado */
  height: 2.2rem;
  background: ${(props) =>
    props.emptyEmail
      ? '#a4a4ad' /* Cinza claro quando o email está vazio */
      : '#000'}; /* Preto */
  border: none;
  color: #fff;
  border-radius: 50%;
  font-size: 1rem; /* Ajustar tamanho do ícone */
  font-weight: bold; /* Deixa a seta em negrito */
  cursor: ${(props) =>
    props.emptyEmail
      ? 'default'
      : 'pointer'}; /* Desabilita o cursor pointer se o email estiver vazio */
  padding: 0;
  display: flex;
  align-items: center;
  justify-content: center;

  &:hover {
    background: ${(props) =>
      props.emptyEmail
        ? '#a4a4ad' /* Cinza claro quando o email está vazio */
        : '#141414'}; /* Preto hover */
  }

  &:disabled {
    cursor: not-allowed;
    opacity: 0.5;
  }
`;

const RegisterLink = styled(Link)`
  display: block;
  margin-top: 1.5rem;
  color: #ea4f97;
  font-size: 1rem;
  text-decoration: none;

  &:hover {
    text-decoration: underline;
  }
`;

const ErrorMessage = styled.div`
  color: #ff0000; /* Vermelho para mensagens de erro */
  margin-top: 1rem;
`;

export default LoginPage;
