import React, { useState } from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);

  const navigate = useNavigate();

  const handleEmailChange = (e) => {
    const emailValue = e.target.value;
    setEmail(emailValue);

    // Resetar para o estado inicial se o email for apagado ou inválido
    if (!emailValue || !validateEmail(emailValue)) {
      setShowPassword(false);
    }
  };

  const handleShowPassword = () => {
    // Mostrar o campo de senha apenas se o email for válido
    if (validateEmail(email)) {
      setShowPassword(true);
    }
  };

  const validateEmail = (email) => {
    // Regex simples para validação de email
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/api/users/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      if (!response.ok) {
        throw new Error('Error logging in');
      }

      const data = await response.json();
      localStorage.setItem('token', data.token);
      navigate('/tasks');
    } catch (error) {
      console.error('Error logging in:', error);
    }
  };

  return (
    <Container>
      <Form onSubmit={handleSubmit}>
        <Title>Sign in to Connecter</Title>
        <EmailContainer>
          <Input
            type="email"
            placeholder="Email"
            value={email}
            onChange={handleEmailChange}
            autoFocus
          />
          {!showPassword && (
            <ShowButton onClick={handleShowPassword}>→</ShowButton>
          )}
        </EmailContainer>
        {showPassword && (
          <PasswordContainer>
            <Input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <SubmitButton type="submit">→</SubmitButton>
          </PasswordContainer>
        )}
      </Form>
    </Container>
  );
};

const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh; /* Altura maior */
  padding: 1rem;
  background: #f0f2f5; /* Cor de fundo */
  border-radius: 12px; /* Bordas arredondadas */
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1); /* Sombra */
`;

const Form = styled.form`
  padding: 2rem;
  max-width: 560px;
  width: 100%;
  text-align: center;
  background: white; /* Cor de fundo do formulário */
  border-radius: 12px; /* Bordas arredondadas no formulário */
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1); /* Sombra */
`;

const Title = styled.h2`
  margin-bottom: 1.5rem;
  font-size: 1.5rem;
  color: #333;
  font-weight: 500;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto,
    'Helvetica Neue', Arial, sans-serif;
`;

const EmailContainer = styled.div`
  position: relative;
  width: 100%;
  margin-bottom: 1rem;
`;

const PasswordContainer = styled.div`
  position: relative;
  width: 100%;
`;

const Input = styled.input`
  width: 70%;
  padding: 0.75rem;
  padding-right: 2.5rem; /* Espaço para o botão */
  border: 1px solid #d0d0d5;
  border-radius: 8px; /* Bordas arredondadas */
  font-size: 1rem;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto,
    'Helvetica Neue', Arial, sans-serif;
  transition:
    border-color 0.3s,
    box-shadow 0.3s;

  &:focus {
    border-color: #007aff;
    box-shadow: 0 0 0 2px rgba(0, 122, 255, 0.2);
    outline: none;
  }
`;

const ShowButton = styled.button`
  position: absolute;
  top: 50%;
  right: 4.2rem; /* Botão no final do input */
  transform: translateY(-50%);
  width: 1.8rem; /* Reduzido o tamanho do botão */
  height: 1.8rem;
  background: #007aff;
  border: none;
  color: #ffffff;
  border-radius: 50%; /* Arredondado */
  font-size: 0.75rem;
  cursor: pointer;
  padding: 0; /* Remover padding extra */
  display: flex;
  align-items: center;
  justify-content: center;

  &:hover {
    background: #0051a8;
  }
`;

const SubmitButton = styled.button`
  position: absolute;
  top: 50%;
  right: 0.5rem; /* Botão no final do input */
  transform: translateY(-50%);
  width: 1.5rem; /* Reduzido o tamanho do botão */
  height: 1.5rem;
  background: #007aff;
  border: none;
  color: #ffffff;
  border-radius: 50%; /* Arredondado */
  font-size: 0.75rem;
  cursor: pointer;
  padding: 0; /* Remover padding extra */
  display: flex;
  align-items: center;
  justify-content: center;

  &:hover {
    background: #0051a8;
  }
`;

export default LoginPage;
