import { useState, useEffect } from 'react';
import { useAuth } from '../../Context/AuthContext';

const useUserInfo = (username) => {
  const [userInfo, setUserInfo] = useState({});
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const { token } = useAuth();

  useEffect(() => {
    const fetchUserInfo = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/manager/users/${encodeURIComponent(username)}`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        if (!response.ok) {
          const errorResponse = await response.json();
          console.error('Erro na resposta da API:', errorResponse); // Adicione este log
          setError('Erro ao carregar informações do usuário');
          return;
        }

        const data = await response.json();
        setUserInfo(data);
      } catch (error) {
        console.error('Erro ao carregar informações do usuário:', error); // Adicione este log
        setError(error.message || 'Erro ao carregar informações do usuário');
      } finally {
        setLoading(false);
      }
    };

    fetchUserInfo();
  }, [username, token]);

  return { ...userInfo, error, loading };
};

export default useUserInfo;
