// Intro.js
import React from 'react';
import styled, { keyframes } from 'styled-components';
import { Link } from 'react-router-dom';

// Animação de entrada para os elementos
const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(-20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const Intro = () => {
  return (
    <IntroContainer>
      <BackgroundVideo autoPlay loop muted playsInline>
        <source src="https://www.videvo.net/videvo_files/converted/2016_10/preview/160622_Work_Team_01.mp471270.mp4" type="video/mp4" />
      </BackgroundVideo>
      <ContentWrapper>
        <Title>Connecter</Title>
        <IntroText>O melhor lugar para gerenciar suas tarefas e muito mais.</IntroText>
        <LoginButton to="/login">Iniciar Sessão</LoginButton>
      </ContentWrapper>
    </IntroContainer>
  );
};

const IntroContainer = styled.div`
  position: relative;
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  overflow: hidden;
  text-align: center;
  background: #000; /* Cor de fundo de fallback */
`;

const BackgroundVideo = styled.video`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  object-fit: cover;
  z-index: -1; /* Fica atrás de todos os elementos */
  opacity: 0.8; /* Transparência para combinar com o tema preto e branco */
`;

const ContentWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 2rem;
  animation: ${fadeIn} 1s ease-in-out forwards;
  z-index: 1; /* Certifica-se de que o conteúdo fique acima do vídeo */
`;

const Title = styled.h1`
  font-size: 5rem;
  color: #fff;
  font-weight: bold;
  margin-bottom: 1.5rem;
  animation: ${fadeIn} 2s ease-out forwards;
  font-family: 'Raleway', sans-serif;
`;

const IntroText = styled.p`
  font-size: 1.5rem;
  color: #ddd;
  margin-bottom: 2rem;
  max-width: 500px;
  animation: ${fadeIn} 2.5s ease-out forwards;
  font-family: 'Raleway', sans-serif;
`;

const LoginButton = styled(Link)`
  background-color: #fff;
  color: #000;
  border: none;
  padding: 1rem 3rem;
  border-radius: 30px;
  font-size: 1.2rem;
  font-weight: 700;
  cursor: pointer;
  transition: transform 0.3s, background-color 0.3s;
  text-decoration: none;
  animation: ${fadeIn} 3s ease-out forwards;

  &:hover {
    transform: scale(1.05);
    background-color: #f0f0f0;
  }

  &:active {
    transform: scale(0.95);
  }
`;

export default Intro;
