// Intro.js
import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';

const Intro = () => {
  return (
    <IntroContainer>
      <BackgroundVideo autoPlay loop muted playsInline>
        <source src="https://videocdn.cdnpk.net/videos/b396b49f-7b00-4e7b-a1d5-6e7f417db965/horizontal/previews/clear/large.mp4?token=exp=1725732621~hmac=bf51ccfd7349190aad768954329ad53312efbc5c94c2f2766d0c03ed17880918" type="video/mp4" />
        Seu navegador não suporta a tag de vídeo.
      </BackgroundVideo>
      <Title>Connecter</Title>
      <LoginButton to="/login">Iniciar Sessão</LoginButton>
      <IntroText>
        O melhor lugar para gerenciar suas tarefas e muito mais.
      </IntroText>
    </IntroContainer>
  );
};

const IntroContainer = styled.div`
  text-align: center;
  padding: 2rem; /* Espaçamento interno para melhor visualização */
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  min-height: 100vh; /* Garante que o conteúdo fique centralizado verticalmente */
  position: relative; /* Necessário para posicionar o vídeo corretamente */
`;

const BackgroundVideo = styled.video`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  object-fit: cover;
  z-index: -1; /* Fica atrás de todos os elementos */
  opacity: 0.7; /* Transparência para combinar com o tema preto e branco */
`;

const Title = styled.h1`
  font-size: 6rem;
  color: #000;
  font-weight: bold;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto,
    'Helvetica Neue', Arial, sans-serif;
  margin-bottom: 1.7rem; /* Espaço entre o título e o texto */
`;

const IntroText = styled.p`
  font-size: 2rem;
  color: #000;
  font-weight: bold;
  margin: 1rem auto;
  max-width: 500px; /* Limita a largura do texto */
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto,
    'Helvetica Neue', Arial, sans-serif;
`;

const LoginButton = styled(Link)`
  background-color: #000; /* Cor do botão */
  color: #fff;
  border: none;
  padding: 0.75rem 2rem;
  border-radius: 20px;
  font-size: 1.1rem;
  font-weight: 600;
  cursor: pointer;
  margin-bottom: 1.7rem;
  transition: background-color 0.3s;
  text-decoration: none;

  &:hover {
    color: #000;
    background-color: gray; /* Cor do botão ao passar o mouse */
  }
`;

export default Intro;
