import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode'; // Corrija a importação se necessário

export const useAuth = () => {
  const [error, setError] = useState(null);
  const [token, setToken] = useState(localStorage.getItem('token') || '');
  const [userInfo, setUserInfo] = useState({
    username: '',
    email: '',
    fullName: '',
    createdAt: '',
  });
  const navigate = useNavigate();

  useEffect(() => {
    if (token) {
      try {
        // Decodifica o token para obter informações do usuário
        const decodedToken = jwtDecode(token);
        console.log('Decoded Token:', decodedToken);

        setUserInfo({
          username: decodedToken.username || 'Usuário',
          email: decodedToken.email || '',
          fullName: decodedToken.fullName || '',
          createdAt: decodedToken.createdAt || '',
        });
      } catch (error) {
        console.error('Erro ao decodificar o token', error);
      }
    }
  }, [token]);

  const login = async (email, password) => {
    try {
      const response = await fetch('http://localhost:5000/api/users/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      if (!response.ok) {
        throw new Error('Erro ao fazer login');
      }

      const data = await response.json();
      localStorage.setItem('token', data.token);
      setToken(data.token);
      navigate(`/tasks/${encodeURIComponent(data.username)}`); // Ajustado para usar o username do response
    } catch (err) {
      setError(err.message);
      console.error('Erro ao fazer login:', err);
    }
  };

  const register = async (username, fullName, email, password) => {
    try {
      const response = await fetch('http://localhost:5000/api/users/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, fullName, email, password }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Erro ao registrar');
      }

      const data = await response.json();
      localStorage.setItem('token', data.token);
      setToken(data.token);
      navigate(`/tasks/${encodeURIComponent(username)}`); // Ajustado para usar o username do request
    } catch (err) {
      setError(err.message);
      console.error('Erro ao registrar:', err);
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    setToken('');
    setUserInfo({
      username: '',
      email: '',
      fullName: '',
      createdAt: '',
    });
    navigate('/login');
  };

  return { login, register, logout, error, token, userInfo };
};
