import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import styled from 'styled-components';
import { useAuth } from '../Context/AuthContext'; 
import jwtDecode from 'jwt-decode'; 

const capitalizeFirstLetter = (string) => {
  return string.charAt(0).toUpperCase() + string.slice(1);
};

const formatDate = (dateString) => {
  const options = {
    day: '2-digit',
    month: '2-digit',
    year: 'numeric',
  };
  return new Date(dateString).toLocaleDateString('pt-BR', options);
};

const TasksPage = () => {
  const { username } = useParams();
  const [tasks, setTasks] = useState([]);
  const [selectedTask, setSelectedTask] = useState(null);
  const [showLoginModal, setShowLoginModal] = useState(false);
  const [fullName, setFullName] = useState('');
  const [email, setEmail] = useState('');
  const [createdAt, setCreatedAt] = useState('');
  const navigate = useNavigate();

  const { token, userInfo } = useAuth();

  useEffect(() => {
    const fetchUserInfo = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/users/user/${username}`);
        if (response.ok) {
          const data = await response.json();
          setFullName(data.fullName || 'Nome não disponível');
          setEmail(data.email || 'Email não disponível');
          setCreatedAt(formatDate(data.createdAt) || 'Data não disponível');
        } else {
          console.error('Erro ao carregar informações do usuário:', response.statusText);
        }
      } catch (error) {
        console.error('Erro ao carregar informações do usuário:', error);
      }
    };

    const fetchTasks = async () => {
      try {
        const apiUrl = token
          ? `http://localhost:5000/api/manager/tasks/${encodeURIComponent(userInfo.username)}`
          : `http://localhost:5000/api/manager/tasks/${encodeURIComponent(username)}`;

        const response = await fetch(apiUrl, {
          headers: token ? { Authorization: `Bearer ${token}` } : {},
        });

        if (response.ok) {
          const data = await response.json();
          setTasks(data);
        } else {
          console.error('Erro ao carregar tarefas:', response.statusText);
        }
      } catch (error) {
        console.error('Erro ao carregar tarefas:', error);
      }
    };

    fetchUserInfo();
    fetchTasks();
  }, [navigate, username, token, userInfo.username]);

  const handleViewTask = (task) => {
    if (token) {
      setSelectedTask(task);
    } else {
      setShowLoginModal(true);
    }
  };

  const handleCloseModal = () => {
    setSelectedTask(null);
  };

  const handleCloseLoginModal = () => {
    setShowLoginModal(false);
  };

  return (
    <Container>
      <WidgetsContainer>
        <UserWidget>
          <h2>Informações de {fullName}</h2>
          <UserInfo>
            <InfoItem>
              <strong>Usuário:</strong> {username}
            </InfoItem>
            <InfoItem>
              <strong>Email:</strong> {email}
            </InfoItem>
            <InfoItem>
              <strong>Data de Criação:</strong> {createdAt}
            </InfoItem>
          </UserInfo>
        </UserWidget>
        <TasksWidget>
          <h2>Tarefas de {fullName}</h2>
          {tasks.length > 0 ? (
            <TaskGrid>
              {tasks.slice(0, 6).map((task) => (
                <TaskItem key={task.id}>
                  <TaskName>{task.taskTitle}</TaskName>
                  <TaskDate>{formatDate(task.createdAt)}</TaskDate>
                  <ActionButton onClick={() => handleViewTask(task)}>
                    Detalhes
                  </ActionButton>
                </TaskItem>
              ))}
            </TaskGrid>
          ) : (
            <NoTasksMessage>Não há tarefas para exibir.</NoTasksMessage>
          )}
        </TasksWidget>
      </WidgetsContainer>

      {selectedTask && (
        <TaskModal onClick={handleCloseModal}>
          <ModalContent onClick={(e) => e.stopPropagation()}>
            <CloseButton onClick={handleCloseModal}>×</CloseButton>
            <ModalHeader>
              <h3>{selectedTask.taskTitle}</h3>
              <ModalDate>{formatDate(selectedTask.createdAt)}</ModalDate>
            </ModalHeader>
            <ModalBody>
              <p><strong>Descrição:</strong> {selectedTask.taskDescription}</p>
              <p><strong>Status:</strong> {selectedTask.taskStatus ? 'Concluído' : 'Pendente'}</p>
            </ModalBody>
          </ModalContent>
        </TaskModal>
      )}
    </Container>
  );
};

const Container = styled.div`
  display: flex;
  justify-content: center;
  background: #000; /* Fundo preto */
  padding: 2rem;
  min-height: 85vh;
  align-items: flex-start;
  color: #fff; /* Texto branco */
`;

const WidgetsContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 2rem;
  width: 100%;
  margin-top: 5rem;
  max-width: 1200px;
  align-items: center;

  @media (min-width: 768px) {
    flex-direction: row;
    justify-content: center;
  }
`;

const Widget = styled.div`
  background: #111; /* Preto escuro */
  border-radius: 10px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
  padding: 2rem;
  width: 100%;
  box-sizing: border-box;
  transition: transform 0.3s ease, box-shadow 0.3s ease;

  &:hover {
    transform: translateY(-4px);
    box-shadow: 0 6px 12px rgba(0, 0, 0, 0.3);
  }
`;

const TasksWidget = styled(Widget)`
  flex: 2;
  max-width: 750px;

  h2 {
    color: #fff;
    font-weight: bold;
    font-size: 1.6rem;
    margin-bottom: 1rem;
  }
`;

const UserWidget = styled(Widget)`
  flex: 1;
  max-width: 350px;

  h2 {
    color: #fff;
    font-weight: bold;
    font-size: 1.6rem;
    margin-bottom: 1rem;
  }
`;

const UserInfo = styled.div`
  font-size: 1rem;
  color: #ccc;

  strong {
    color: #fff;
  }
`;

const InfoItem = styled.div`
  margin-bottom: 0.5rem;
`;

const TaskGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 1rem;
  width: 100%;
`;

const TaskItem = styled.div`
  background: #222;
  border-radius: 10px;
  padding: 1rem;
  text-align: center;
  transition: transform 0.3s ease, box-shadow 0.3s ease;
  width: 100%;
  height: auto;

  &:hover {
    transform: translateY(-4px);
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.3);
  }
`;

const TaskName = styled.h3`
  color: #fff;
  font-weight: bold;
  font-size: 1.4rem;
  margin-bottom: 0.5rem;
`;

const TaskDate = styled.p`
  color: #888;
  font-size: 0.9rem;
`;

const ActionButton = styled.button`
  background: #ea4f97; /* Rosa */
  color: #fff;
  border: none;
  border-radius: 5px;
  padding: 0.5rem 1rem;
  margin-top: 1rem;
  cursor: pointer;
  transition: background 0.3s ease;

  &:hover {
    background: #f70073; /* Rosa mais escuro */
  }
`;

const TaskModal = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.8);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 1000;
`;

const ModalContent = styled.div`
  background: #333;
  padding: 2rem;
  border-radius: 10px;
  max-width: 600px;
  width: 90%;
`;

const CloseButton = styled.button`
  position: absolute;
  top: 1rem;
  right: 1rem;
  background: none;
  border: none;
  color: #fff;
  font-size: 1.5rem;
  cursor: pointer;
`;

const ModalHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 1rem;
`;

const ModalDate = styled.p`
  font-size: 0.9rem;
  color: #888;
`;

const ModalBody = styled.div`
  color: #ddd;
  font-size: 1rem;
  line-height: 1.6;

  p {
    margin-bottom: 1rem;
  }

  strong {
    color: #fff;
  }
`;

const NoTasksMessage = styled.p`
  text-align: center;
  font-size: 1rem;
  color: #ccc;
`;

export default TasksPage;
