import React, { useState, useEffect } from 'react';
import { useAuth } from '../hooks/useAuth';
import Navbar from '../components/Manager/Navbar';
import CreateTaskModal from '../components/Manager/CreateTaskModal';
import DropdownMenu from '../components/Manager/DropdownMenu';

const TaskManager = () => {
  const [tasks, setTasks] = useState([]);
  const [filter, setFilter] = useState('all');
  const [modalOpen, setModalOpen] = useState(false);
  const [currentTask, setCurrentTask] = useState(null);
  const { token, userInfo } = useAuth();

  useEffect(() => {
    const fetchTasks = async () => {
      if (!token || !userInfo?.username) return;
      try {
        const response = await fetch(`http://localhost:5000/api/manager/tasks/${userInfo.username}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        if (!response.ok) throw new Error('Erro ao buscar tarefas');
        const data = await response.json();
        setTasks(data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchTasks();
  }, [token, userInfo?.username]);

  const handleAddTaskClick = () => {
    setCurrentTask(null);
    setModalOpen(true);
  };

  const handleSaveTask = async (task) => {
    try {
      const method = currentTask ? 'PUT' : 'POST';
      const url = currentTask ? `http://localhost:5000/api/manager/tasks/${currentTask.id}` : 'http://localhost:5000/api/manager/tasks';
      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(task),
      });

      if (!response.ok) throw new Error('Erro ao salvar tarefa');
      const savedTask = await response.json();

      if (currentTask) {
        setTasks(tasks.map(t => (t.id === savedTask.id ? savedTask : t)));
      } else {
        setTasks(prevTasks => [...prevTasks, savedTask]);
      }

      setModalOpen(false);
      setCurrentTask(null);
    } catch (error) {
      console.error(error);
    }
  };

  const handleCompleteTask = async (taskId) => {
    try {
      const response = await fetch(`http://localhost:5000/api/manager/tasks/${taskId}/complete`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) throw new Error('Erro ao marcar tarefa como concluída');
      const updatedTask = await response.json();
      setTasks(tasks.map(task => (task.id === updatedTask.id ? updatedTask : task)));
    } catch (error) {
      console.error(error);
    }
  };

  const handleTaskClick = (task) => {
    setCurrentTask(task);
    setModalOpen(true);
  };

  return (
    <div>
      <Navbar>
        <DropdownMenu />
        <button onClick={handleAddTaskClick}>Adicionar Tarefa</button>
      </Navbar>
      <main>
        <h1>Gerenciador de Tarefas</h1>
        <CreateTaskModal
          isOpen={modalOpen}
          onClose={() => setModalOpen(false)}
          onCreate={handleSaveTask}
          token={token}
          task={currentTask}
        />
        <ul>
          {tasks
            .filter(task => filter === 'all' || task.status === filter)
            .map(task => (
              <li key={task.id} onClick={() => handleTaskClick(task)}>
                <span>{task.taskTitle}</span>
                <button onClick={() => handleCompleteTask(task.id)}>Concluir</button>
              </li>
            ))}
        </ul>
      </main>
    </div>
  );
};

export default TaskManager;
